content: "[[module CSS]]

span.skew {
  display:inline-block;
  animation: skew 1.5s infinite;
}

@keyframes skew {

  0% {
    transform: none;
  }
  33% {
    transform: none;
  }
  33.3% {
    transform: skewX(45deg);
  }
  33.4% {
    transform: skewX(-45deg);
  }
  33.5% {
    transform: none;
  }
  66% {
    transform: none;
  }
  66.3% {
    transform: skewX(20deg);
  }
  66.4% {
    transform: skewX(-20deg);
  }
  66.5% {
    transform: none;
  }
  77% {
    transform: none;
  }
  77.3% {
    transform: skewX(30deg);
  }
  77.4% {
    transform: skewX(-30deg);
  }
  77.5% {
    transform: none;
  }

}

@keyframes fade-in {
    0% {opacity: 0;}
    50% {opacity: 0;}
    100% {opacity: 1;}}

/* Credit to Nanimono Daemon for the mouseover blackbox format, found here:
http://www.scp-wiki.net/scp-style-resource#toc11 */

.dblock {background: white; color: white; transition: 1s;}
.dblock:hover {color: #333; transition: 1.5s;}

/* Credit to James Kanjo for the inline hover tip CSS I butchered, found here:
http://community.wikidot.com/howto:inline-hover-tips */

.hover1 {color: #333; transition: 1s;}
.hover1:hover {color: white; transition: 1s;}
.hover1 span {display: none;}
.hover1:hover span {position: absolute; display: inline; background-color: transparent; color: #333; animation: fade-in 1s;}
.hover1:hover span span {position: relative;}

[[/module]]

