content:
[[div style="display:none"]]
[[form]]
fields:
  original:
    type: text
    label: 元作品:
    hint: scp-xxx-jp(URLのscp-jp.wikidot.com/より後の部分)

  deleted:
    label: 元記事削除済:
    type: select
    values:
      true: yes
      false: no
    default: false

  branch:
    label: 元支部:
    type: select
    values:
      1: JP(日本語)
      2: EN(英語)
      3: RU(ロシア語)
      4: KO(韓国語)
      5: CN(中国語)
      6: FR(フランス語)
      7: PL(ポーランド語)
      8: ES(スペイン語)
      9: TH(タイ語)
      10: DE(ドイツ語)
      11: IT(イタリア語)
      12: UA(ウクライナ語)
      13: PT/BR(ポルトガル語)
      14: CS(チェコ語)
      15: ZH-TR(繁体中国語)
      101: INT(英語-支部間共著)
      102: VN(ベトナム語)
      103: TR(トルコ語)
      104: HE(ヘブライ語)
      105: ND(北欧諸言語)
      106: EL(ギリシャ語)
      107: NL(オランダ語)
      108: HU(ハンガリー語)
      109: LA(ラテン語)
      110: ET(エストニア語)
      111: CY(ウェールズ語)
      112: ID(インドネシア語)
      113: SL(スロベニア語)
      114: RO(ルーマニア語)

  series1:
    type: select
    values:
      0: メインシリーズ
      1: 001提言
      2: Joke SCPシリーズ
      3: Explained SCPシリーズ
    default: 0

  hint1:
    type: static
    value: "**メインシリーズの場合のみ、以下からシリーズ番号を選択してください:**"

  series2:
    type: select
    values:
      0: ""
      1: シリーズ1
      2: シリーズ2
      3: シリーズ3
      4: シリーズ4
      5: シリーズ5
      6: シリーズ6
      7: シリーズ7
    default: 0

  content:
    label: フレーバーテキスト
    type: wiki
    hint: フレーバーテキストを入力
    height: 7

  move:
    label: 移転
    type: select
    values:
      false: 使用しない
      true: 使用する
    default: false

  originauthor:
    type: text
    hint: ※許可無く使用しないでください
    match: /[^、-◯ぁ-ん]*/
    match-error: "無効な記号です"
    join: true

  redirect-b:
    type: hidden
    value: '[[module Redirect destination="/scp-flavor'

  redirect-e:
    type: hidden
    value: '"]]'

[[/form]]
[[/div]]
[[module css]]
.form-value.field-firstpub {
  display: inline-block;
}

.form-row {
  line-height: 3em;
}

.form-table {
  border-spacing: 0px;
}

.row-0, .row-3, .row-4, .row-5, .row-6 {
  background: rgba(0, 0, 0, 0.05);
}

.row-0 .form-label {
  font-size: 0px;
}

.row-0 .form-label::after {
  font-size: .8rem;
  content: "ページタイトル:";
  display:inline;
}

.customquote {
  text-align: left;
  display: inline-block;
  margin: 1em auto;
  border-left: solid 2px gray;
  padding-left: 1em;
  line-height: 3em;
}

@media(max-width:768px) {
  .form-labels, .form-values {
    display: block;
  }
}

/* [name="page-top"][target="_blank"]は、ページ編集時のみ現れます */
[name="page-top"][target="_blank"] + #container-wrap-wrap #action-area > h1:first-child {
    font-size: 0;
}
[name="page-top"][target="_blank"] + #container-wrap-wrap #action-area > h1:first-child::before {
    content: 'ファイル情報の入力';
    font-size: 1.5rem;
}

.row-7 {
  display: none;
}

.form-row {
  line-height: 5em;
}

.radio-inline {
  display: inline-block;
  padding: 10px;
}

.a-content > p {
    margin-top: 0;
}

.error-inline {
    border: none;
    color: #333;
    font-size: 0;
    user-select: none;
}

.error-inline em {
    font-size: 0.8rem;
    font-style: normal;
    font-weight: bold;
    user-select: text;
}

.error-inline em:after {
    content: " (アカウント削除済み)";
    font-weight: normal;
}

.flavor-wrap > div > p {
    margin: 0;
}

.flavor-wrap {
    border: solid 1px rgba(0, 0, 0, 0.2);
    border-radius: 15px;
    overflow: hidden;
}

.flavor-wrap .flavor-link {
    background: rgba(0, 0, 0, 0.05);
    padding: 8px;
}

.flavor-wrap .flavor-content {
    padding: 8px;
}
[[/module]]
