[[/div]]
====
[[div style="display:none"]]
[[form]]
fields:
  original:
    type: text
    label: 元作品:
    match: /^[a-z0-9:-]+$/
    match-error: 小文字英字/数字/ハイフン(-)/コロン(:)のみ使用可能です
    hint: scp-xxx-jp(URLのscp-jp.wikidot.com/より後の部分)

  deleted:
    label: 元記事削除済:
    type: select
    values:
      true: yes
      false: no
    default: false

  branch:
    label: 元支部:
    type: select
    values:
      1: JP(日本語)
      2: EN(英語)
      3: RU(ロシア語)
      4: KO(韓国語)
      5: CN(中国語)
      6: FR(フランス語)
      7: PL(ポーランド語)
      8: ES(スペイン語)
      9: TH(タイ語)
      10: DE(ドイツ語)
      11: IT(イタリア語)
      12: UA(ウクライナ語)
      13: PT/BR(ポルトガル語)
      14: CS(チェコ語)
      15: ZH-TR(繁体中国語)
      101: INT(英語-支部間共著)
      102: VN(ベトナム語)
      103: TR(トルコ語)
      104: HE(ヘブライ語)
      105: ND(北欧諸言語)
      106: EL(ギリシャ語)
      107: NL(オランダ語)
      108: HU(ハンガリー語)
      109: LA(ラテン語)
      110: ET(エストニア語)
      111: CY(ウェールズ語)
      112: ID(インドネシア語)
      113: SL(スロベニア語)
      114: RO(ルーマニア語)

  series1:
    type: select
    values:
      0: メインシリーズ
      1: 001提言
      2: Joke SCPシリーズ
      3: Explained SCPシリーズ
    default: 0

  hint1:
    type: static
    value: "**メインシリーズの場合のみ、以下からシリーズ番号を選択してください:**"

  series2:
    type: select
    values:
      0: ""
      1: シリーズ1
      2: シリーズ2
      3: シリーズ3
      4: シリーズ4
      5: シリーズ5
      6: シリーズ6
      7: シリーズ7
      8: シリーズ8
      9: シリーズ9
    default: 0

  content:
    label: フレーバーテキスト
    type: wiki
    hint: フレーバーテキストを入力
    height: 7

  move:
    label: 移転
    type: select
    values:
      false: 使用しない
      true: 使用する
    default: false

  originauthor:
    type: text
    hint: ※許可無く使用しないでください
    match: /[^、-◯ぁ-ん]*/
    match-error: "無効な記号です"
    join: true

  redirect-b:
    type: hidden
    value: '[[module Redirect destination="/scp-flavor'

  redirect-e:
    type: hidden
    value: '"]]'

[[/form]]
[[/div]]
