[[mOdUlE cSs]]
#u-fademodule, #page-options-container {   
    opacity: 0.1; transition: all 0.5s ease-out;
}

#u-fademodule:hover, #page-options-container:hover {  
    opacity: 1;
}

body, #main-content, #container {
    background: #222;
    color: #FC3BE4;
    text-shadow: 0 0 0.1rem #FC3BE4;
    font-family:'VonwaonBitmap 12px', sans-serif;
}

.page-tags, .page-rate-widget-box #search-top-box, #top-bar, #side-bar, #login-status, #page-title, #header, #footer, #license-area, #footer, #license-area {
    display: none;
}

#main-content {
    margin: 0 auto;
}

#footer {
    background: #222;
    color: #222;
    text-shadow: none;
}

#footer a {
    color: #222;
}

a {
    color: #FC3BE4;
}
a:visited {
    color: #FC3BE4;
}

#lock-info, .content modal-body, .content, .button-bar modal-footer a, .title modal-header {
    background: #111 !important;
}

#content-wrap {
    min-height: 100vh !important;
}

/* 评分模组↓ */
.page-rate-widget-box .rate-points {
    background-color: #222 !important;
    border: solid 1px #222;
    text-transform: capitalize;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #222;
    border-top: solid 1px #222;
    border-bottom: solid 1px #222;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #FC3BE4;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #FC3BE4 !important;
    color: #222 !important;
}
.page-rate-widget-box .cancel {
    background-color: #222;
    border: solid 1px #222;
}
.page-rate-widget-box .cancel a {
    color: #FC3BE4;
}
.page-rate-widget-box .cancel a:hover {
    background: #FC3BE4;
    color: #222;
}

#page-content .rate-box-with-credit-button {
    background-color: #222;
    border: 1px solid #222;
    border-radius: 0px !important;
}

.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}

#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
     color: #222;
     background-color: #FC3BE4;
}

.close-credits, .credit-back {
    filter: hue-rotate(260deg);
}

div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #FC3BE4 !important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: #FC3BE4 !important;
    color: #222 !important;
}

div.rate-box-with-credit-button {
    box-shadow: none;
}

div.creditButton a {
    color: #FC3BE4 !important;
}

div.creditButton a:hover {
    color: #222 !important;
}

/* 著作信息↓ */
.modalbox {
    background: #222 !important;
    box-shadow: none !important;
}

.modalbox a {
    color: #FC3BE4 !important;
}

/* 全屏↓ */
#u-full_Screen:target {
    width: 100vw;
    height: 100vh;
    transition: width 1s, height 1s;
    transition-delay: 0.5s;
    position: fixed;
    top: 5vh;
    left: 0;
}
[[/module]]

