[[module CSS]]
/*
    Raisa Theme
    [2018 Wikidot Theme]
    Created by 7happy7

*/


/* ///////////global/////////// */
.modalbox,
.owindow,
.scp-image-caption,
.wiki-content-table th,
#toc,
.hovertip,
body {
  background-color: #141212 !important;
  background-image: linear-gradient(rgba(18, 16, 16, 0.03) 50%, rgba(0, 0, 0, 0.45) 50%), linear-gradient(90deg, rgba(255, 0, 0, 0.22), rgba(0, 255, 0, 0.11), rgba(0, 0, 255, 0.22));
  background-size: 100% 2px, 3px 100%;
  color: #3F3;
  box-shadow: none !important;
font-family: Arial !important;
}
#u-credit-view,
#odialog-container {
  z-index: 999;
}
.wiki-content-table th,
.wiki-content-table td {
  max-width: 40%;
}

#top-bar {
    width: 100%;
    margin: 0 auto;
}

.mobile-top-bar {
    display: none;
    position: absolute;
    left: 1em;
    bottom: 0px;
    z-index: 0;
}

.owindow .title {
    background-color: #666;
}

h1,
h2,
h3,
h4,
h5,
h6,
#page-title {
    font-family: 'Julius Sans One', 'Noto Serif JP',  sans-serif;
    color: #3F3;
    border-color: #8D8;
}
a,a:visited {
   color: #8D8;
   transition: text-shadow 0.4s ease;
}

#page-content a:hover {
    animation: blink 0.8s ease-in-out infinite alternate;
}

a:hover,
#page-content a:hover {
   text-shadow: -6px 0px 8px rgba(255, 255, 255, 0.83), 6px 0px 8px rgba(255, 255, 255, 0.83);
   text-decoration-line:none;
}

@keyframes blink {
    0% { text-shadow: none; }
    50%, 100% { text-shadow: -1px 0px 2px rgba(255, 255, 255, 0.83), 1px 0px 2px rgba(255, 255, 255, 0.83) }
}

#page-content .hide a {
    animation: none!important;
    text-shadow: none!important;
}

hr {
   background-color: transparent;
   border: none;
   border-bottom: 1px dashed #8D8;
}
blockquote {
   background-color: rgba(255, 255, 255, 0.05);
}
#lock-info,
#edit-sections-button-hovertip,
#edit-append-button-hovertip,
#history-button-hovertip,
#discuss-button-hovertip,
#files-button-hovertip,
#site-tools-button-hovertip,
#backlinks-button-hovertip,
#rename-move-button-hovertip,
#view-source-button-hovertip,
#parent-page-button-hovertip,
#abuse-report-button-hovertip,
#bug-report-button-hovertip,
#wikidot-help-button-hovertip,
#wikidot-tos-button-hovertip,
#wikidot-privacy-button-hovertip,
#edit-button-hovertip,
.code {
    color: #333;
}
div#container-wrap {
    background-image: none;
}



/* ///////////HEADER/////////// */
#header h1, #header h2 {
    margin-left: 0;
}

#header {
    background: none !important;
}

#header h1 {
    background: url('http://scpsandbox-pl.wdfiles.com/local--files/projekt-kino/banerzygard') no-repeat top left;
    background-size: contain;

    display: block;

    height: 130px;
    max-height: 130px;

    width: 100%;
    max-width: 550px;

    font-size: 0;
    position: relative;
    top: 32px;
}
#header h1 a {
    display: inline-block;
    left: 50%;
    padding: 80px 0px 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Julius Sans One', sans-serif;
    font-size: 220%;
    font-weight: 100;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 8%;
    overflow: hidden;
}
#header h1 a span {
    display: none;
}
#header h1 a::before {
    [[image https://i.ibb.co/XfPmny2/pracowniazygarda.png]];
    color: rgba(51, 255, 51, 0.6);
    bottom: -2em;
}
#header h2 {
display: inline-block;
position: absolute;
bottom: 30%;
left: 0;
right: 0;
margin: 0 auto;
}
#header h2 span{
font-size: 0px;
padding: 5px;

}



#search-top-box-input {
display: none;
}

#search-top-box-form input[type=submit] {
color: #3F3;
font-size: 90%;
border: solid 1px #3F3;
background-color: transparent;
background-image: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
color: #8D8;
border: solid 1px #8D8;
background-color: rgba(255,245,255,0.1);
background-image: none;
}

/* TOP MENU */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}

#top-bar ul {
    float: right;
}

#top-bar li {
    margin: 0;
}

#top-bar a {
    background: transparent;
    color: #3F3;
}

#top-bar ul li {
    border: 0;
    position: relative;
}

#top-bar ul li ul {
    border: solid 1px #404040;;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}

#top-bar ul li a {
    border-left: solid 1px #404040 ;
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #414141;
    color: #3F3;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid transparent;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #404040;
    text-decoration: none;
    color: #3F3;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #3F3;
}

.top-bar ul li:last-of-type ul {
    right: 0;
}

/* IE7 HACK */
#top-bar ul > li > ul {    
    *margin-top: -4px;
}

/* ///////////side-bar/////////// */
#side-bar .side-block {
    border: none;
    box-shadow: none !important;
    background-color: rgba(255,245,255,0.2) !important;
    border-radius: 3px;
}
#side-bar .heading {
    color: #eee;
    border-bottom: solid 1px #eee;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #eee;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #eee;
}

/* ///////////page-rate-widget/////////// */
.page-rate-widget-box {
    color: transparent;
    border-radius: 0;
    box-shadow: none;
}
.page-rate-widget-box .rate-points {
    background-color: transparent !important;
    color: #3F3 !important;
    border: 0;
    border-bottom: solid 1px #3F3;
    border-radius: 0;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: transparent;
    border: 0;
    border-bottom: solid 1px #3F3;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #8D8 !important;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: rgba(255,245,255,0.05);
    color: #9E9 !important;
}
.page-rate-widget-box .cancel {
    background-color: transparent;
    border: 0;
    border-bottom: solid 1px #3F3;
    border-right: solid 1px #3F3;
    border-radius: 0 0 5px 0;
}
.page-rate-widget-box .cancel a {
    color: #8D8 !important;
}
.page-rate-widget-box .cancel a:hover {
    background: rgba(255,245,255,0.05);
    color: #9E9 !important;
}
.rate-box-with-credit-button .creditButton {
    background-color: transparent !important;
    border: 0 !important;
    border-bottom: solid 1px #3F3 !important;
    border-right: solid 1px #3F3 !important;
    border-radius: 0 0 5px 0 !important;
    box-shadow: none !important;
}
.creditButton a {
    color: #8D8 !important;
}
.creditButton a:hover {
    background: rgba(255,245,255,0.05);
    color: #9E9 !important;
}
.credit-license a.cc {
    color: #fff !important;
}
.modalbox .modalbox-title {
    background: #666 !important;
}

/* ///////////YUI-TABS/////////// */
.yui-navset {
    overflow: hidden;
}

.yui-navset li{
    line-height: 141%;
    word-break:break-all;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border:none;
    background-color: rgba(255,255,255,0.05);
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent ! important; /* tab background */
    background-image: none;
    border-color:#3F3;
    color:#3F3;
    transition: 0.125s;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background-image: none;
}

.yui-navset .yui-nav a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px rgba(0,0,0,0.5);
    background-color: rgba(255,255,255,0.05);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: rgba(255,255,255,0.25); /* selected tab background */
    border-color:#3F3;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    background-color: rgba(255,255,255,0.05);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    border-color:#3F3;
}

.yui-navset .yui-content {
    background-color: rgba(255,255,255,0.05);

}

.yui-navset .yui-content:before {
  content: " ";
  position: absolute;
  top: 10px;
  bottom: 0;
  left: 0;
  right: 0;
  width: 45%;
  height: auto;
  margin: auto;
  background-image: url( "http://unofficial-scp-sandbox-jp.wikidot.com/local--files/modal-glitch/scp_trans.png" );
  background-size: contain;
  background-position: center center;
  background-repeat: no-repeat;
  animation: spin1 20s linear infinite;
    z-index: -1;

}
@keyframes spin1 {
    0% {transform: rotate(0deg);}
    100% {transform: rotate(360deg);}
}


.yui-navset .yui-content scp-image-block img {
    position: relative;
    z-index: 100;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border: none;
}

.yui-navset-left .yui-content {
    border-left-color: #b7a9a9; /* different border color */
}

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected,
.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    margin: 0;
    padding: 0;
}

/* CONTENT */
#main-content {
    margin: 0 2em 0 22em;
    padding: 1em;
    position: relative;
}

/* ACTUALLY HIDE HIDDEN TAGS */
#main-content .page-tags a[href^="/system:page-tags/tag/_"]
{
    display: none;
}

#breadcrumbs {
    margin: -1em 0 1em;
    font-weight: 85%;
}

/* ///////////General Media Query/////////// */
@media (max-width: 1025px) {
.open-menu a {
    position: fixed;
    bottom: 0.5em;
    left: 0.5em;
    z-index: 15;
    font-family: 'Nanum Gothic', san-serif;
    font-size: 30px;
    font-weight: 700;
    width: 30px;
    height: 30px;
    line-height: 0.9em;
    text-align: center;
    border: none !important;
    background-color: transparent !important;
}

.open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -moz-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -ms-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    -o-box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
    box-shadow: 0px 0px 20px 3px rgba(153,153,153,1);
}

#main-content {
    margin: 0 5%;
    padding: 0;
    -webkit-transition: 0.5s ease-in-out 0.1s;
    -moz-transition: 0.5s ease-in-out 0.1s;
    -ms-transition: 0.5s ease-in-out 0.1s;
    -o-transition: 0.5s ease-in-out 0.1s;
    transition: 0.5s ease-in-out 0.1s;
}

#side-bar {
    display: block;
    position: fixed;
    top: 0;
    left: -25em;
    width: 17em;
    height: 100%;
    overflow-y: auto;
    z-index: 10;
    background-color: transparent !important;
    padding: 1em 1em 0 1em;
    -webkit-transition: left 0.5s ease-in-out 0.1s;
    -moz-transition: left 0.5s ease-in-out 0.1s;
    -ms-transition: left 0.5s ease-in-out 0.1s;
    -o-transition: left 0.5s ease-in-out 0.1s;
    transition: left 0.5s ease-in-out 0.1s;
}

#side-bar:after {
    content: "";
    position: absolute;
    top: 0;
    width: 0;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.2);

}

#side-bar:target {
    display: block;
    left: 0;
    width: 17em;
    margin: 0;
    z-index: 10;
}

#side-bar:target + #main-content {
    left: 0;
}

#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: rgba(0,0,0,0.3) 1px 1px repeat
    z-index: -1;
}
}

a:visited {color: #8d8!important;}
.modalbox-title h2 {
font-size: 0px;
}
.modalbox-title h2:before {
content: "credit";
font-size: 20px;
}
#u-side-staff .side-block {
border: none;
box-shadow: none !important;
background-color: rgba(255,245,255,0.2) !important;
border-radius: 3px;
}
#u-side-staff .heading {
color: #eee;
border-bottom: solid 1px #eee;
}
#u-side-staff .collapsible-block-unfolded-link {
