[[module CSS]]
.danke{
padding: 5px 5px 5px 15px;
margin: auto;
margin-top:1em;
margin-bottom:1em;
width: auto;
font-family: monospace;
font-size: 1em;
}

.red{
background-color:#220000;
border: 3px solid #AA5555;
color: #dd1f1b;
}

.blue{
background-color:#000022;
border: 3px solid #5555AA;
color: #0076fd;
}

.hover {
    background-color: rgb(var(--dark-accent));
    padding: 0 1px;
}
.hover:hover {

}

.hover span {    display: none;}
.hover:hover span {
    position: absolute;
    display: inline;
    margin: 0px 0px;
    height: auto;
    width: auto;
    background: #fff;
    border: 2px solid rgb(var(--bright-accent));
    color: rgb(var(--bright-accent));;
    padding: 1em;
}
.hover:hover span span {
    position: relative;
    margin: auto;
    height: auto;
    width: auto;
    border: none;
    padding: 0;
}

