    --height: 90px;
  }
  .ra-title, .ra-subtitle {
    display: none;
  }
  .ra-metatitle {
    display: block;
  }
}

@media (max-width: 767px) {
  :root{ 
    --width: 95vw;
  }
  .right {
    margin-top: 0;
    margin-left: 0;
  }
  #side-bar {
    top: 0em;
    background-color: #DDD;
  }
  #search-top-box-input {
    display: none;
  }
}

@media (max-width: 589px) {
  :root{  
    --size: 220%;
    --height: 90px;
  }
  .ra-title, .ra-metatitle {
    letter-spacing: -0.5px;
  }
  .ra-subtitle {
    line-height: 15px;
  }
  #extra-div-1, #header h1 {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
  }
  #header h2 {
    position: absolute;
    left: 50%;
    transform: translateX(-115px);
    top: 100px;
  }
  #login-status {
    width: 100%;
    color: transparent;
  }
  #login-status .printuser {
    color: #888;
  }
  #login-status #my-account {
    position: absolute;
    right: 25px;
  }
  #login-status #account-topbutton {
    position: absolute;
    right: 0;
  }
  #login-status .login-status-sign-in {
    float: right;
  }
  .endcomment {
    font-size: 80%;
  }
}

@media (max-width: 385px) {
  .ra-title, .ra-metatitle {
    letter-spacing: -1px;
    font-size: 90%;
  }
  .ra-subtitle {
    letter-spacing: 0;
  }
}

