  border-color: #0E183C;
  box-sizing: border-box;

  background-image: url('http://scpko.wdfiles.com/local--files/scp1000kocontesthub/banner.jpg');
  background-attachment: fixed;
  background-position: center top;
  background-size: 100%;
}

.right {
  position: sticky;
  top: 0px;
  left: 0px;
  margin-top: -15px;
  margin-left: -40%;
}

.left {
  margin-left: -100%;
  margin-top: -2px;
}

.ra-content {
  display: block;
  width: calc( 100vw - 50px );
  margin-left: 0;
  margin-top: -2px;
  text-align: center;
  padding: 10px;
  color: #FFF;
  text-shadow: 2px 0 #049, -2px 0 #049, 0 2px #049, 0 -2px #049, 1px 1px #049, -1px 1px #049, 1px -1px #049, -1px -1px #049;
}

.ra-content h1 {
  font-family: 'OKCHAN', san-serif;
  font-weight: 400;
  line-height: 0;
  font-size: var(--size);
  color: #FFF;
  text-shadow: 0 -3px 1.5px #049, 0 3px 1.5px #049, 3px 0 1.5px #049, -3px 0 1.5px #049, 2px 2px 1.5px #049, 2px -2px 1.5px #049, -2px 2px 1.5px #049, -2px -2px 1.5px #049;
}

.ra-subtitle {
  font-family: 'GongGothicMedium', san-serif;
}

.ra-metatitle {
  display: none;
}

h1 {
  font-family: 'GongGothicMedium', san-serif;
  color: #049;
}

h2 {
  font-family: 'GongGothicMedium', san-serif;
  line-height: 1.6;
}

a, a:visited, a.newpage, a:hover {
  color: #0060DF;
}

#top-bar ul li:hover a {
  background: #FFF;
  color: #0060DF;
}

#side-bar a, #side-bar a:visited, #side-bar a.newpage {
  color: #0060DF;
}

#side-bar a:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
  color: #049;
}

#side-bar {
  top: calc( var(--height) + 40px );
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
  color: #0060DF;
}

#side-bar .heading {
  color: #049;
  border-color: #049;
}

#side-bar .side-block {
  background-color: #FFF !important;
}

#side-bar .side-block:nth-child(2),
#side-bar .side-block:nth-child(4),
#side-bar .side-block:nth-child(5) {
  background-color: #E5E5FF !important;
}

#header, #top-bar {
  width: 90% !important;
}

#header h1 {
  margin-left: 0;
}

#header h2 {
  margin-left: 70px;
}

#header h1 a {
  font-family: 'OKCHAN', san-serif;
  color: transparent;
  text-shadow: none;
  font-size: 180% !important;
}

#header h1 a::before {
  content: 'SCP-KO';
  white-space: nowrap;
  color: #000;
  text-shadow: 0 -3px 1.5px #EEE, 0 3px 1.5px #EEE, 3px 0 1.5px #EEE, -3px 0 1.5px #EEE, 2px 2px 1.5px #EEE, 2px -2px 1.5px #EEE, -2px 2px 1.5px #EEE, -2px -2px 1.5px #EEE;
}

#header h2 span {
  font-family: 'GongGothicMedium', san-serif;
  color: transparent;
  text-shadow: none;
}

#header h2 span::before {
  content: 'Since 2013';
  color: #000;
  text-shadow: 1.5px 0 #EEE, -1.5px 0 #EEE, 0 1.5px #EEE, 0 -1.5px #EEE, 1px 1px #EEE, -1px 1px #EEE, 1px -1px #EEE, -1px -1px #EEE;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus {
  background-color: transparent;
}

#search-top-box-form input[type="submit"] {
  background: #0060DF;
  color: #FFF;
}

#search-top-box-form input[type=submit]:hover, #search-top-box-form input[type=submit]:focus {
  background: #0060DF;
  color: #FFF;
}

#login-status, #login-status a, #login-status ul a {
  color: #888;
}

div.wd-adunit {
  display: none;
}

@media (max-height: 600px) {
  :root {
    --height: 90px;
  }
  .ra-title, .ra-subtitle {
    display: none;
  }
  .ra-metatitle {
    display: block;
  }
}

@media (max-width: 767px) {
  :root{ 
    --width: 95vw;
  }
  .right {
    margin-top: 0;
    margin-left: 0;
  }
  #side-bar {
    top: 0em;
    background-color: #DDD;
  }
  #search-top-box-input {
    display: none;
  }
}

@media (max-width: 479px) {
  :root{  
    --size: 250%;
    --height: 90px;
  }
  .ra-title, .ra-metatitle {
    letter-spacing: -0.5px;
  }
  .ra-subtitle {
    line-height: 15px;
  }
  #extra-div-1, #header h1 {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
  }
  #header h2 {
    position: absolute;
    left: 50%;
    transform: translateX(-115px);
    top: 100px;
  }
  #login-status {
    width: 100%;
    color: transparent;
  }
  #login-status .printuser {
    color: #888;
  }
  #login-status #my-account {
    position: absolute;
    right: 25px;
  }
  #login-status #account-topbutton {
    position: absolute;
    right: 0;
  }
  #login-status .login-status-sign-in {
    float: right;
  }
  .endcomment {
    font-size: 80%;
  }
}

@media (max-width: 385px) {
  .ra-title, .ra-metatitle {
    letter-spacing: -1px;
    font-size: 90%;
  }
  .ra-subtitle {
    letter-spacing: 0;
  }
}

