[[module CSS]]

sup {

vertical-align: top; position: relative; top: -0.5em;
}

.CustomTabRJC .collapsible-block {
    box-sizing: border-box;
}

body {
    background: rgb(255, 255, 255);
    color: rgb(0, 0, 0);
}

#search-top-box {
  top: -999px;
}

a,a:visited {
    color: rgb(255, 80, 80);
}



div#container-wrap {
    background: url(http://scp-jp.wikidot.com/local--files/goede-film-hub/%E3%82%B2%E3%83%BC%E3%83%87%E3%83%98%E3%83%83%E3%83%80%E3%83%BC.jpg) top left repeat-x;
}

div#header {
    background: url(http://scp-jp.wikidot.com/local--files/goede-film-hub/%E3%82%B2%E3%83%BC%E3%83%86%E3%83%AD%E3%82%B4.png) 10px 40px no-repeat;
    background-position: 1em -2em; background-size 88px 88px;
}

div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: " ";
    color: #eee;
    text-shadow: 1px1px1px rgba(0,0,0,.8);
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: " ";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0,3 .8);
    white-space: pre;
}



.body {
  font-family: 'Varela', sans-serif;
  color: rgb(255, 255, 255);
  font-size:15px;
  background: rgb(80,80,80);
}

.yui-nav{
border:#FFF !important;
color:#EEE !important;
background-color:#000 !important;
padding:1px;
}
.yui-nav li a em{
border:solid #FFF 1px !important;
color:#EEE !important;
background-color:#000 !important;
}

.yui-nav li.selected a em{
border:#FFF !important;
color:#000 !important;
background-color:#EEE !important;
}
.yui-navset{
background-color:#000 !important;
}
.yui-content{
border:solid #FFF 2px !important;
background-color:#000 !important;
color:#EEE !important;
}

