[[module css]]

/*-------------------------------*/

.mither {
    font-family: var(--header-font);
    color: green;
    font-size: 1.375em;
    line-height: 1.275;
}
.mither p { line-height: inherit; }

.scenesetter {
    padding-inline: 1.75rem;
    position: relative;
    padding-block: 1.25rem;
    isolation: isolate;
}
.scenesetter::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background-image: var(--header-image);
    background-size: cover;
    background-position: center;
    opacity: 0.195;
    box-shadow: inset 0 0 4rem 3.25rem rgb(var(--foamy-white));
    filter: blur(1.375rem);
    z-index: -1;
}

.ultraspace {
    padding-block: 3.5rem;
}


.glasscase{
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}
.glassyeye{
    aspect-ratio : 1 / 1;
    width: 50%;
    margin-top: -24px;
    position: relative;
    transition: 0.5s !important;
}
.glassyeye:hover{
    filter: hue-rotate(180deg);
}
.glassyeye img{
    height: 100%;
    width: 100%;
    object-fit: cover;
    object-position: center;
}
.allfolks{
    position: absolute;
    z-index: 5;
    top: 0px;
    left: 10px;
    font-size: 4vh;
    mix-blend-mode: difference;
    filter: invert(1) grayscale(1) contrast(9);

.allfolks a{
    font-weight: bold;
    color:red !important; /*ADD SPECIFICALLY THIS TO CSS*/
}

@media screen and (min-width: 1200px) {
.allfolks{
    font-size: 25px;
}
}
@media screen and (max-width: 640px) {
.glasscase{
    flex-direction: column;
}
.allfolks{
    font-size: 4vw;
    top: 3px;
}
.glassyeye{
    margin-top: 0px;
    width: 100%;
}
}


