[[module CSS]]

#header h2 span::before {
  content: "제01K기지 아날로그 기록보관소";
}

.floatbox {
  position: relative;
  max-width: unset !important;
  width: calc( 100% + 30px );
  padding: 2em;
  margin: 25px 0;
  box-sizing: border-box;
  border: 2px solid black;
  background: #F0F0EA;
}

.floatbox .collapsible-block-folded,
.floatbox .collapsible-block-unfolded {
  text-align: center;
  position: relative;
}

.floatbox .collapsible-block {
  position: relative;
  z-index: 10;
}

.floatbox .collapsible-block-unfolded::before,
.floatbox .collapsible-block-unfolded::after {
  content: "";
  width: calc( 100% + 55px );
  height: calc( 100% + 5em );
  border: 2px solid black;
  display: inline-block;
  position: absolute;
  top: -30px;
  left: -30px;
}

.floatbox:not(.long) .collapsible-block-unfolded::before,
.floatbox:not(.long) .collapsible-block-unfolded::after {
  height: calc( 100% + 55px );
}

.left .collapsible-block-unfolded::before {
  background: #efefef;
  transform: rotate(-0.35deg);
  z-index: 2;
}

.right .collapsible-block-unfolded::before {
  background: #efefef;
  transform: rotate(0.35deg);
  z-index: 2;
}

.left:not(.long) .collapsible-block-unfolded::before {
  transform: rotate(-1deg);
}

.right:not(.long) .collapsible-block-unfolded::before {
  transform: rotate(1deg);
}

.floatbox .collapsible-block-unfolded::after {
  background: #FFF url(http://scpko.wdfiles.com/local--files/thd-glasses-hub/scp_ko_bg.png) center no-repeat;
  background-size: 30em;
  -webkit-background-size: 30em;
  filter: brightness(1.01);
  z-index: 5;
}

.floatbox .collapsible-block-link {
  font-family: 'YES24', sans-serif;
  font-size: 20px;
  font-weight: 500;
}

.collapsible-block-content {
  text-align: left;
}

.floatbox .collapsible-block-link,
.floatbox .collapsible-block-content {
  position: relative;
  z-index: 10;
}

.floatbox h2 {
  margin: 15px 0;
}

.floatbox h3 {
  margin: 15px;
}

.left {
  margin-left: -30px;
}

.left .collapsible-block-link {
  margin-left: 30px;
}

.right .collapsible-block-link {
  margin-left: -30px;
}

.scp-image-block {
  position: relative;
}

.scp-image-block.block-right {
  margin: 0 0 1em 2em;
  transform: rotate(-3deg);
}

.scp-image-block::after {
  content: "";
  position: absolute;
  top: 25px;
  right: 0;
  width: 80px;
  height: 22px;
  border: 6px ridge #C33;
  border-radius: 22px;
  transform: rotate(-20deg) translateX(66px);
  z-index: 15;
}

.scp-image-block .enlarge {
  position: relative;
  z-index: 10;
}

.scp-image-block:hover .enlarge {
  z-index: 100;
  box-shadow: 0 5px 30px #333;
}

.file {
  position: relative;
  border: solid 2px #000000;
  padding: 20px 15px;
  margin: 10px 10px 10px 10px;
  background: #DDC;
  z-index: 1;
  color: #333;
}

.file::before {
  content: "";
  position: absolute;
  top: 0;
  right: 0;
  width: 30px;
  height: 200px;
  transform: translateX(34px);
  background: #DDC;
  z-index: 0;
  border: 2px solid #000;
  border-radius: 0 25px 25px 0;
}

#page-content .file hr {
  background: #333;
}

.file-header {
  width: 100%;
  text-align: center;
  padding: 0;
  margin: auto;
}

.file-header th, .file-header td {
  text-align: center;
  padding: 10px 30px;
  word-break: keep-all;
}

.file-header th {
  background: rgba(255,255,255,0.5);
}

.file-header td {
  background: rgba(255,255,255,0.8);
}

@media (max-width: 525px) {
  .file-header h1 {
    font-size: 18px;
  }

  .file-header th, .file-header td {
    padding: 5px 10px;
  }
}

[[/module]]
