[[module CSS]]
.mcdiv_earthworm {
  height: 5em;
  width: 0em;
  margin: auto;
  position: relative;
  display: block;
}

.mcdiv_earthworm .mcdiv_prev, .mcdiv_earthworm .mcdiv_next {
  color: white;
  position: absolute;
  top: 1em;
  height: 1em;
  white-space: nowrap;
  display: block;
  background-image: linear-gradient(to bottom, rgb(53, 173, 30), rgb(40, 160, 20) calc(0.2em + 2px), rgb(151, 104, 21) calc(0.2em + 2px), rgb(90, 61, 12));
  background-position: 0 -2px;
  background-size: 100% calc(100% + 4px);
  background-clip: border-box;
  border: 2px solid rgba(0, 0, 0, 0.3);
  border-radius: 0.3em;
}

.mcdiv_earthworm .mcdiv_prev {
  right: 0em;
  padding: 0.5em 3em 0.5em 1.5em;
  transition: padding-right 0.6s;
}

.mcdiv_earthworm .mcdiv_prev:hover {
  padding-right: 3.2em;
}

.mcdiv_earthworm .mcdiv_next {
  left: 0em;
  padding: 0.5em 1.5em 0.5em 3em;
  transition: padding-left 0.6s;
}

.mcdiv_earthworm .mcdiv_next:hover {
  padding-left: 3.2em;
}


.mcdiv_earthworm a, .mcdiv_earthworm a:hover, .mcdiv_earthworm a:visited {
  color: white;
}

.mcdiv_earthworm .mcdiv_center::after{
  content: ' ';
  background-image: url('https://scp-wiki.wdfiles.com/local--files/scp-6468/mcdiv_logo.png');
  background-size: 100% auto;
  background-repeat: no-repeat;
  width: 5em;
  height: 5em;
  display: block;
  position: absolute;
  left: -2.5em;
  top: 0em;
  transition: transform 0.6s;
  transition-timing-function: ease-in-out;
}

.mcdiv_earthworm .mcdiv_center:hover::after {
  transform: rotate(360deg);
}
[[/module]]

