[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Source+Code+Pro:ital,wght@0,300;0,400;0,500;1,300;1,400;1,500&display=swap');

.logo .image-block,
.logo .scp-image-block {
    box-shadow: none !important;
}

.logo .image-block .image-caption,
.logo .scp-image-block .scp-image-caption {
    box-shadow: none;
    margin-top: 10px;
    border-radius: 3px;
    border-top: none;
    clip-path: polygon(4% 0%, 100% 0%, 96% 100%, 0% 100%);
}

.insignia {
    width: 140px;
    filter: drop-shadow(0 0 0.75rem #db7a70);
    transition: all 0.25s ease-in-out;
}

@media not (prefers-reduced-motion) {
    .insignia:hover {
        transform: rotate(15deg);
    }
}

.email {
    background: white;
    border: solid 2px rgb(var(--medium-accent));
    width: 90%;
    padding: 10px;
    margin: auto;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5)
}

.tofrom {
    margin-left: 10px;
    margin-top: 5px;
    padding: 1px 15px;
    border-left: solid 3px maroon;
}

.talk-page-box {
    margin: 4px 0;
    border: 1px solid #c0c090;
    width: 50%;
    transition: all 0.25s ease-in-out;
}

.talk-page-box:hover {
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.talk-page-box .box-image {
    border: none;
    padding: 0.4em;
    text-align: center;
}

.talk-page-box .box-text {
    border: none;
    padding: 0.25em 0.9em;
    width: 100%;
}

.dossier {
    background: white;
    margin: 2rem auto;
    padding: 1.25rem;
    border: 3px solid rgb(var(--bright-accent));
    box-shadow: inset 0px 0px 4px rgba(var(--medium-accent), 0.5)
}

.dossier h1,
.dossier h2,
.dossier h3,
.dossier h4,
.dossier h5,
.dossier h6 {
    text-align: center;
}

.terminal {
    font-family: 'Source Code Pro', monospace;
    background: black;
    color: white;
    border: 3px solid gray;
    padding: 0 10px;
}

.dossier > .scp-image-block {
    border-radius: 8px;
    border: solid rgb(38, 38, 38) 3px;
}

.dossier > .scp-image-block > img {
    border-radius: 4px 4px 0px 0px;
}

.dossier > .scp-image-block > .scp-image-caption {
    border-radius: 0px 0px 4px 4px;
}

.dossier .collapsible-block {
    display: block;
    text-align: center;
    padding: 0.75em;
    background: #cacaca45;
    border-top: 0.125rem dashed rgb(var(--bright-accent));
    border-bottom: 0.125rem dashed rgb(var(--bright-accent));
}

.dossier .collapsible-block .collapsible-block-content {
    text-align: left;
}

.date {
    color: lightgray;
    font-size: 80%;
}

.doc {
    margin: auto;
}

.blockquote .doc {
    top: -2.3em;
    margin-bottom: -3.75rem;
}
[[/module]]
