[[module css]]
@import url("https://fonts.googleapis.com/css?family=Libre+Barcode+128+Text&display=swap&subset=latin-ext");

#page-content > .myTitle *, #page-content > .myClass *, #page-content > .myRemarks *, #page-content > .myPreview * {display: none !important;}
.myTitle .myTitle, .myClass .myClass {display: inline !important;}
.myRemarks .myRemarks, .myPreview .myPreview {display: block !important;}

.main-content .heading {
  display: block;
  font-family: 'Libre Barcode 128 Text', cursive;
  font-size: 3.5em;
  text-align: center;
}
.main-content .heading > span {
  display: inline-block;
  padding: 0.5em 1.5em;
  background-color: rgb(12, 12, 12);
  color: rgb(252, 252, 252);
  -webkit-clip-path: var(--clip-path);
  clip-path: var(--clip-path);
}

.main-content .heading:nth-of-type(7n+1) > span {
  --clip-path: polygon(
    0.62em 0.18em,
    calc(100% - 0.80em) 0.11em,
    calc(100% - 0.33em) calc(100% - 0.10em),
    0.25em calc(100% - 0.18em)
  );
}
.main-content .heading:nth-of-type(7n+2) > span {
  --clip-path: polygon(
    0.58em 0.24em,
    calc(100% - 0.01em) 0.11em,
    calc(100% - 0.16em) calc(100% - 0.12em),
    0.34em calc(100% - 0.12em)
  );
}
.main-content .heading:nth-of-type(7n+3) > span {
  --clip-path: polygon(
    0.40em 0.11em,
    calc(100% - 0.69em) 0.25em,
    calc(100% - 0.15em) calc(100% - 0.13em),
    0.95em 100%
  );
}
.main-content .heading:nth-of-type(7n+4) > span {
  --clip-path: polygon(
    0.83em 0.20em,
    100% 0.06em,
    calc(100% - 1.20em) 100%,
    0.82em calc(100% - 0.25em)
  );
}
.main-content .heading:nth-of-type(7n+5) > span {
  --clip-path: polygon(
    1.08em 0.06em,
    calc(100% - 0.50em) 0.15em,
    calc(100% - 1.16em) calc(100% - 0.13em),
    0.26em calc(100% - 0.11em)
  );
}
.main-content .heading:nth-of-type(7n+6) > span {
  --clip-path: polygon(
    0.62em 0.02em,
    calc(100% - 0.64em) 0.13em,
    calc(100% - 0.03em) calc(100% - 0.16em),
    0.93em calc(100% - 0.14em)
  );
}
.main-content .heading:nth-of-type(7n+7) > span {
  --clip-path: polygon(
    0.22em 0.03em,
    calc(100% - 0.37em) 0.10em,
    calc(100% - 1.19em) calc(100% - 0.08em),
    0.47em calc(100% - 0.24em)
  );
}

.main-content .list-pages-item {
  -webkit-perspective: 500;
  perspective: 500;
}

.main-content .list-pages-item > div {
  padding: 2em;
  background-color: rgb(12, 12, 12);
  -webkit-clip-path: var(--clip-path);
  clip-path: var(--clip-path);
  -webkit-transform: var(--transform);
  -moz-transform: var(--transform);
  transform: var(--transform);
  -webkit-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  -moz-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  -ms-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  -o-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}
.main-content .list-pages-item > div:hover {
  background-color: rgb(214, 0, 0);
}

.main-content .list-pages-item:nth-of-type(7n+1) > div {
  --clip-path: polygon(
    0.75em 1.81em,
    calc(100% - 1.46em) 1.89em,
    calc(100% - 1.10em) calc(100% - 1.61em),
    0.77em calc(100% - 0.93em)
  );
  --transform: rotateX(-6.3deg) rotateY(-6.0deg);
}
.main-content .list-pages-item:nth-of-type(7n+2) > div {
  --clip-path: polygon(
    1.36em 0.77em,
    calc(100% - 1.08em) 1.57em,
    calc(100% - 0.17em) calc(100% - 0.93em),
    0.67em calc(100% - 1.87em)
  );
  --transform: rotateX(7.6deg) rotateY(-11.9deg);
}
.main-content .list-pages-item:nth-of-type(7n+3) > div {
  --clip-path: polygon(
    1.20em 0.07em,
    calc(100% - 0.33em) 1.85em,
    calc(100% - 0.02em) calc(100% - 1.13em),
    0.90em calc(100% - 1.62em)
  );
  --transform: rotateX(-10.5deg) rotateY(7.2deg);
}
.main-content .list-pages-item:nth-of-type(7n+4) > div {
  --clip-path: polygon(
    1.70em 0.70em,
    calc(100% - 1.56em) 0.59em,
    calc(100% - 1.63em) calc(100% - 0.07em),
    0.19em calc(100% - 1.02em)
  );
  --transform: rotateX(-8.1deg) rotateY(7.0deg);
}
.main-content .list-pages-item:nth-of-type(7n+5) > div {
  --clip-path: polygon(
    0.70em 0.85em,
    calc(100% - 0.59em) 0.76em,
    calc(100% - 1.93em) calc(100% - 1.30em),
    1.73em calc(100% - 1.27em)
  );
  --transform: rotateX(1.0deg) rotateY(11.5deg);
}
.main-content .list-pages-item:nth-of-type(7n+6) > div {
  --clip-path: polygon(
    0.59em 0.35em,
    calc(100% - 1.72em) 0.29em,
    calc(100% - 0.19em) calc(100% - 1.69em),
    1.99em calc(100% - 1.25em)
  );
  --transform: rotateX(10.6deg) rotateY(-2.0deg);
}
.main-content .list-pages-item:nth-of-type(7n+7) > div {
  --clip-path: polygon(
    0.37em 0.05em,
    calc(100% - 1.22em) 0.89em,
    100% calc(100% - 1.05em),
    1.16em calc(100% - 0.12em)
  );
  --transform: rotateX(-6.3deg) rotateY(-8.7deg);
}

.main-content .list-pages-item > div > div {
  padding: 0.5em 1.5em;
  background-color: rgb(252, 252, 252);
  -webkit-clip-path: var(--clip-path);
  clip-path: var(--clip-path);
  -webkit-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1), color 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  -moz-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1), color 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  -ms-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1), color 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  -o-transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1), color 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  transition: background-color 0.5s cubic-bezier(0.4, 0, 0.2, 1), color 0.8s cubic-bezier(0.4, 0, 0.2, 1);
}
.main-content .list-pages-item > div:hover > div {
  background-color: rgb(66, 66, 72);
  color: rgb(252, 252, 252);
}

.main-content .list-pages-item:nth-of-type(7n+1) > div > div {
  --clip-path: polygon(
    0.27em 0.56em,
    calc(100% - 0.08em) 0.88em,
    calc(100% - 0.72em) calc(100% - 0.08em),
    0.05em calc(100% - 0.12em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+2) > div > div {
  --clip-path: polygon(
    0.84em 0.68em,
    calc(100% - 0.24em) 0.32em,
    calc(100% - 0.33em) calc(100% - 0.42em),
    0.63em calc(100% - 0.57em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+3) > div > div {
  --clip-path: polygon(
    0.06em 0.24em,
    calc(100% - 0.89em) 0.70em,
    calc(100% - 0.65em) calc(100% - 0.29em),
    0.32em calc(100% - 0.61em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+4) > div > div {
  --clip-path: polygon(
    0.78em 0.98em,
    calc(100% - 0.24em) 0.36em,
    calc(100% - 0.38em) calc(100% - 0.74em),
    0.67em calc(100% - 0.85em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+5) > div > div {
  --clip-path: polygon(
    0.90em 0.35em,
    calc(100% - 0.50em) 0.31em,
    calc(100% - 0.65em) calc(100% - 0.73em),
    0.12em calc(100% - 0.38em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+6) > div > div {
  --clip-path: polygon(
    0.27em 0.12em,
    calc(100% - 0.32em) 0.76em,
    100% calc(100% - 0.74em),
    0.95em calc(100% - 0.46em)
  );
}
.main-content .list-pages-item:nth-of-type(7n+7) > div > div {
  --clip-path: polygon(
    0.06em 0.18em,
    calc(100% - 0.77em) 0.14em,
    calc(100% - 0.68em) calc(100% - 0.16em),
    0.33em calc(100% - 0.71em)
  );
}

.main-content .list-pages-item > div > div a {
  -webkit-transition: color 1s cubic-bezier(0.4, 0, 0.2, 1);
  -moz-transition: color 1s cubic-bezier(0.4, 0, 0.2, 1);
  -ms-transition: color 1s cubic-bezier(0.4, 0, 0.2, 1);
  -o-transition: color 1s cubic-bezier(0.4, 0, 0.2, 1);
  transition: color 1s cubic-bezier(0.4, 0, 0.2, 1);
}
.main-content .list-pages-item > div:hover > div a,
.main-content .list-pages-item > div:hover > div a:visited,
.main-content .list-pages-item > div:hover > div a:hover {
  color: rgb(214, 0, 0);
}

.main-content .list-pages-item > div > div > p:first-of-type {
  font-weight: bold;
  font-size: 1.5em;
  text-shadow: 0.0625rem 0.0625rem 0 rgb(252, 252, 252),
    -0.0625rem 0.0625rem 0 rgb(252, 252, 252),
    0.0625rem -0.0625rem 0 rgb(252, 252, 252),
    -0.0625rem -0.0625rem 0 rgb(252, 252, 252),
    0 0.0625rem 0 rgb(252, 252, 252),
    0 -0.0625rem 0 rgb(252, 252, 252),
    -0.0625rem 0 0 rgb(252, 252, 252),
    0.0625rem 0 0 rgb(252, 252, 252),
    0.125rem 0.125rem 0 rgb(252, 252, 252),
    -0.125rem 0.125rem 0 rgb(252, 252, 252),
    0.125rem -0.125rem 0 rgb(252, 252, 252),
    -0.125rem -0.125rem 0 rgb(252, 252, 252),
    0 0.125rem 0 rgb(252, 252, 252),
    0 -0.125rem 0 rgb(252, 252, 252),
    -0.125rem 0 0 rgb(252, 252, 252),
    0.125rem 0 0 rgb(252, 252, 252),
    0.0625rem 0.125rem 0 rgb(252, 252, 252),
    -0.0625rem 0.125rem 0 rgb(252, 252, 252),
    0.0625rem -0.125rem 0 rgb(252, 252, 252),
    -0.0625rem -0.125rem 0 rgb(252, 252, 252),
    0.125rem 0.0625rem 0 rgb(252, 252, 252),
    -0.125rem 0.0625rem 0 rgb(252, 252, 252),
    0.125rem -0.0625rem 0 rgb(252, 252, 252),
    -0.125rem -0.0625rem 0 rgb(252, 252, 252);
}

.main-content .list-pages-item > div > div > p:first-of-type,
.main-content .list-pages-item > div:hover > div > p:first-of-type {
  color: rgb(12, 12, 12);
}
