[[module CSS]]
@charset "utf-8";

/*@import url('http://scp-wiki.wikidot.com/local--files/component:3lawtheme/station-font.css');*/

/*
    Third Law Canon Theme
    [2016 Wikidot Theme]
    Created by GreenWolf, with technical assistance from Randomini and aesthetic assistance from Dr Kens and tawny.
    Based on SCP Sigma 9 Theme created by Aelanna and Dr Devan.
    The Station font was created by Nick Matavka and is licensed under the SIL Open Font License.
    The Prometheus Labs/Third Law Flame Logo was created by GreenWolf and is licensed under CC-BY-SA 3.0.
    Edited for SCP基金会 by Izsac
    以及
    SCP-4319
    Powered on 2019/07/04 18:46:21
    [2019 Wikidot Theme]
    Created by DianaBerry
    CC BY-SA 3.0
    ColorlessL 基于以上版式修改

*/
 @charset "utf-8";
@import url('https://fonts.lug.ustc.edu.cn/css?family=Courgette&display=swap');
@import url('https://fonts.googleapis.com/css?family=ZCOOL+KuaiLe&display=swap');
/* COMMON */
:root {
    font-size: 1.05em;
}
#content-wrap {
    position: relative;
    margin: 2em auto 0;
    max-width: 1040px;
    min-height:1300px;
    height: auto !important;
    height: 1500px;
}
blockquote{
    -webkit-margin-start: 0px;
    -webkit-margin-before: 1em;
    -webkit-margin-after: 1em;
    -webkit-margin-end: 0px;
    border: 1px solid #888;
    border-radius: 1px;
    background-color: #E3E7EA;
}
h1, #page-title {
    color: #660047;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
h1 {
    margin-top: 0.7em;
    padding: 0;
    font-weight: bold;
font-family: tegaki,'Courgette','ZCOOL Kuaile', normal;
}
h2, h3, h4, h5, h6, h7 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
font-family: tegaki,'Courgette','ZCOOL Kuaile', normal;
}
#page-title {
    border-color: #660047;
}
ul {
    /* list-style-image: url(http://d3g0gp89917ko0.cloudfront.net/v--3b8418686296/common--theme/shiny/images/bullet.gif); */
    list-style: square;
}
#top-bar ul {
    /* list-style-image: none; IE ONLY! IF list-style-image IS SET */
}

li, p {
    line-height: 141%;
}
/* links */
a {
    color: #D90098;
    text-decoration: none;
    background: transparent;
}
a.newpage {
    color: #FF00B3;
    text-decoration: none;
    background: transparent;
}

a:visited {
    color: #8C0062;
    text-decoration: none;
    background: transparent;
}

a:hover {
    text-decoration: underline;
    background-color: transparent;
}

.form-control {
width: 95%;
}

/* GLOBAL WIDTH */
#header, #top-bar {
    width: 90%;
    max-width: 980px;
    margin: 0 auto;
}

#top-bar {
    width: 100%;
    margin: 0 auto;
}

body {
    background-color: #ffe3ee;
font-family: tegaki,'Courgette','ZCOOL Kuaile', normal;
    font-size: 0.80em;
    color: #ff69ab;
}
/* HEADER */
#search-top-box-input {
    background-color: #ffa3eb;
    border: dotted 1px #ff69ab;
}
#search-top-box input.empty {
    color: ##ff69ab;
}
#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #ffa3eb;
    border: dotted 1px #ff69ab;
/*Nico was here. */
}
#search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
    background: #ffa3eb !important;
    border: solid 1px #ff69ab;
    color: #ff69ab;
}
#search-top-box-form input[type="submit"], #search-top-box-form input[type="submit"] {
    background: #ffa3eb !important;
    border: si 1px #ff69ab;
    color: #ff69ab;
}

#login-status {
    color: #660047;
    font-size: 90%;
    z-index: 30;
}
#login-status a {
    background: transparent;
    color: #eb4991;
}
#login-status ul a {
    color: #eb4991;
    background: transparent;
}

#account-topbutton {
    background: #CCCCCC;
    color: #eb4991;
}

.printuser img.small {
    margin-right: 1px;
}
div#header h1 a span {
    font-size: 0px;
}
div#container-wrap {
    background: url(http://kaktuskontainer.wdfiles.com/local--files/sigma%3Atheme-test-page/header.png) top left repeat-x;
}
div#header {
    background: url(http://kaktuskontainer.wdfiles.com/local--files/sigma%3Atheme-test-page/heart.png) no-repeat;
    background-position: 0em 1em;
    background-size: 150px
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "純純少女網";
    font-family: tegaki,'Courgette','ZCOOL Kuaile', normal;
    color: #ff5ca5;
    text-shadow: #eb4991;
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}

div#header h2:after {
    content: "桃色の心、桃色娘のために";
    font-family: tegaki,'Courgette', 'ZCOOL Kuaile', normal;
    font-weight: bold;
    color: #ff5ca5;
    padding: 25px 0;
    text-shadow: #eb4991;
    white-space: pre;
    margin: 4;
}
/* TOP MENU */
#top-bar {
    position: absolute;
    z-index: 50;
    top: 140px;
    height: 21px;
    line-height: 18px;
    padding: 0;
    z-index: 20;
    font-size: 90%;
}

#top-bar ul {
    float: right;
}

#top-bar li {
    margin: 0;
}

#top-bar a {
    color: #e9ebf0;
    background: transparent;
}

#top-bar ul li {
    border: 0;
    position: relative;
}

#top-bar ul li ul {
    border: solid 1px #660047;
    box-shadow: 0 2px 6px rgba(0,0,0,.5);
    border-top: 0;
}

#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #E3E7EA;
    color: #4682B4;
    border-left: solid 1px rgba(64,64,64,1);
    border-right: solid 1px rgba(64,64,64,1);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    width: 150px;
    border-top: 1px solid #6A7194;
    line-height: 160%;
    height: auto;
    max-height: none;
    padding-top: 0;
    padding-bottom: 0;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #E2E4E7;
    text-decoration: none;
}
#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}
#top-bar ul li ul li, #top-bar ul li ul li.sfhover,
#top-bar ul li ul li, #top-bar ul li ul li:hover {
    border-width: 0;
}
#top-bar ul li ul li a {
    border-width: 0;
}
#top-bar ul li ul a, #top-bar a:hover {
    color: #4682B4;
}

.top-bar ul li:last-of-type ul {
    right: 0;
}

/* IE7 HACK */
#top-bar ul > li > ul {
    *margin-top: -4px;
}

/* SIDE MENU */
#side-bar {
    clear: none;
    float: none;
    position: absolute;
    top: 0.5em;
    left: 2em;
    width: 17em;
    padding: 0;
    border: none;
    display: block;
    background: transparent;
}

#side-bar a:visited {
   color: #ffe3ee!important;
}
 #side-bar a{
   color: #ffe3ee!important;
}
#side-bar img {
     filter: hue-rotate(180deg) saturate(300%) brightness(10%) invert(100%);
    -webkit-filter: hue-rotate(180deg) saturate(300%) brightness(10%) invert(100%);
}

#side-bar div.side-block:nth-child(5) > div:nth-child(2) > div:nth-child(1) img {
    filter: none;
    -webkit-filter: none;
}

div.scpnet-interwiki-wrapper {
    filter:  hue-rotate(180deg) saturate(300%) brightness(10%) invert(100%);
}

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #fff0f6;
    border-radius: 10px;
    background: #ff69ab !important;
    box-shadow: #f0bdd1 !important;
    margin-bottom: 15px;
    border-radius: 5px;
}
#side-bar .side-area {
    padding: 10px;
}
#side-bar .heading {
    color: #660047;
    border-bottom: solid 1px #660047;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}
#side-bar p {
    margin: 0;
}
#side-bar div.menu-item {
    margin: 2px 0;
}
#side-bar div.menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}

#side-bar div.menu-item a {
    font-weight: bold;
}
#side-bar div.menu-item.inactive img {
    opacity: 0.25;
}
#side-bar div.menu-item.inactive a {
    color: #ffe3ee!important;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #660047;
}
#side-bar div.menu-item.sub-item {
}

#side-bar .collapsible-block-folded {
    background: none;
}

#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #660047;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    color:#ffe3ee!important;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color:#ffe3ee!important;
    text-decoration: none;
}

#side-bar ul{
    list-style-type: none;
    padding: 0 5px 0;
}
/* CONTENT */
#main-content {
    margin: 0 2em 0 22em;
    padding: 1em;
    position: relative;
}

/* ACTUALLY HIDE HIDDEN TAGS */
#main-content .page-tags a[href^="/system:page-tags/tag/_"]
{
    display: none;
}

#breadcrumbs {
    margin: -1em 0 1em;
    font-weight: 85%;
}
 /* YUI-TABS */
.yui-navset .yui-content{
    background-color: #ffa3eb;
    padding-left: 2em;
    padding-right: 2em;
    border: 1px solid black;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #36648B;
    border: 1px solid black;
}
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #36648B; /* selected tab background */
    color: #eeeeee;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #bfc3d4;
    text-decoration: none;
    border: 1px solid black;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border: 0px solid black;
}
.yui-navset li {
    line-height: normal;
}

/* FOOTER */
#footer {
    clear: both;
    font-size: 77%;
    background: #7F7C94;
    color: #C1BCE0;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer .options {
    visibility: visible;
    display: block;
    float: right;
    width: 50%;
    font-size: 100%;
    text-align: right;
}
#footer a {
    color: #ffe3ee;
    background: transparent;
}


/* Custom page content classes */
#page-content {
    min-height: 720px;
}

.unmargined > p {
    margin: 0;
    line-height: 100%;
}
.content-panel {
    border: solid 1px #888880;
    border-radius: 10px;
    background-color: #1F1F1F;
    margin: 10px 0 15px;
    box-shadow: 3px 3px 6px #BBBBBB;
    box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
}
.content-panel.standalone {
    background: #E0E0E0;
}
.content-panel.series {
    padding: 0 20px;
    margin-bottom: 20px;
}
.content-panel.centered {
    text-align: center;
}
.content-panel.left-column {
    float: left;
    width: 48%;
}
.content-panel.right-column {
    float: right;
    width: 48%;
}

.content-panel .panel-heading {
    padding: 2px 10px;
    color: #E0E0E0;
    font-size: 90%;
    font-weight: bold;
    text-shadow: 1px 1px 2px rgba(0,0,0,.35);
}
.content-panel .panel-heading > p,
.content-panel .panel-footer > p {
    margin: 0;
}
.content-panel .panel-body {
    padding: 5px 10px;
    background: #E0E0E0;
    /* box-shadow: 1px 2px 3px rgba(0,0,0,.2) inset */
}
.content-panel .panel-footer {
    padding: 1px 10px;
    color: #E0E0E0;
    font-size: 80%;
    font-weight: bold;
    text-align: right;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.content-panel .panel-footer a {
    color: #E0E0E0;
}
.content-panel .content-toc {
    float: right;
    padding: 0 20px;
    background-color: #E0E0E0;
    border: solid 1px #CCCCCC;
    border-radius: 10px;
    margin: 20px 0 5px 5px;
    white-space: nowrap;
    box-shadow: inset 1px 2px 6px rgba(0,0,0,.15)
}

.alternate:nth-child(even) {
    background-color: rgba(255,255,255,.9);
}

/* Page Rating Module Customizations */
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #ff69ab!important;
    border: solid #ffa3c7 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #ffa3c7!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #ffe3ee;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #ffa3eb!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color:  #EF4B80!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}

/* Fixes for multi-line page tags */

#main-content .page-tags {
    margin: 1em 0 0;
    padding: 0;
}
#main-content .page-tags span {
    display: inline-block;
    padding: 0;
    max-width: 60%;
}
#main-content .page-tags a {
    display: inline-block;
    white-space: nowrap;
}

/* Standard Image Block */
.scp-image-block {
    box-shadow: 0 3px 6px rgba(0,0,0,.35);
    width: 300px;
}

.scp-image-block.block-right {
        float: right;
    clear: right;
    margin: 0 2em 1em 2em;
}

.scp-image-block.block-left {
    float: left;
    clear: left;
    margin: 0 2em 1em 0;
}

.scp-image-block.block-center {
    margin-right: auto;
    margin-left: auto;
}
.scp-image-block img {
    border: 0;
    width: 300px;
}
.scp-image-block .scp-image-caption {
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
    width: 300px;
}
.scp-image-block > p {
    margin: 0;
}
.scp-image-block .scp-image-caption > p {
    margin: 0;
    padding: 0 10px;
}

/* Forum Customizations */
.forum-thread-box .description-block {
    padding: .5em 1em;
    border-radius: 10px;
    box-shadow: 0 1px 5px rgba(0,0,0,.15),
    inset 0 1px 0 rgba(255,255,255,.8),
    inset 0 10px 5px rgba(255,255,255,.25),
    inset 0 -15px 30px rgba(0,0,0,.1)
}

.thread-container .post .head {
    padding: 0.5em 1em;
    background-color: #EEEEEE;
    background: linear-gradient(to right, #EEEEEE, #ECECEE);
    box-shadow: inset 2px 3px 6px rgba(0,0,0,.15);
    border-radius: 5px 5px 0 0;
}

/* Hide Forum Signatures */
.signature {
    display:none !important;
}

/* Ruby by Nanimono Demonai */

.ruby, ruby {
    display:inline-table;
    text-align:center;
    white-space:nowrap;
    line-height:1;
    height:1em;
    vertical-align:text-bottom;
}

.rt, rt {
    display:table-header-group;
    font-size:0.6em;
    line-height:1.1;
    text-align:center;
    white-space:nowrap;
}

/* Keycap */
 keycap {
    border: 1px solid;
    border-color: #ddd #bbb #bbb #ddd;
    border-bottom-width: 2px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    background-color: #f9f9f9;
    padding: 1px 3px;
    font-family: inherit;
    font-size: 0.85em;
    white-space: nowrap;
}

/* tag style */

.tags {
display: inline-block;
margin: 0 0 0 5px;
padding: 3px 5px 3px 0px;
height: 13px;
line-height: 13px;
font-size: 11px;
background: #666;
color: #fff;
text-decoration: none;
-moz-border-radius-bottomright: 4px;
-webkit-border-bottom-right-radius: 4px;
border-bottom-right-radius: 4px;
-moz-border-radius-topright: 4px;
-webkit-border-top-right-radius: 4px;
border-top-right-radius: 4px;
}

.tags:before {
content: "";
padding: 0px 1px 3px 1px;
float: left;
position: relative;
top: -3px;
left: -10px;
width: 0;
height: 0;
border-color: transparent #666 transparent transparent;
border-style: solid;
border-width: 8px 8px 8px 0px;
}

.tags:after {
content: "";
position: relative;
top: 4.5px;
left: -8px;
float: left;
width: 4px;
height: 4px;
-moz-border-radius: 2px;
-webkit-border-radius: 2px;
border-radius: 2px;
background: #fff;
-moz-box-shadow: -1px -1px 2px #004977;
-webkit-box-shadow: -1px -1px 2px #333;
box-shadow: -1px -1px 2px #333;
}

/* Display Black Block by Nanimono Demonai */
.bblock {
    color: #000000;
    background-color:#000000;
    transition: 2s;
    text-decoration: none;
}

.bblock:hover {
    background-color:#000000;
    color: #006600;
    text-decoration: none;
}

.dblock {
    color:#000000;
    background-color:#000000;
    transition: 2s;
    text-decoration: none;
}

.dblock:hover {
    background-color:transparent;
    text-decoration: none;
}

/*
    2011-11-13 Minobe Hiroyuki @ Marguerite Site
    www.marguerite.jp/Nihongo/WWW/CSSTips/EmphasizeDots-CSS3.html
    Edited for the SCP Foundation by Nanimono_Demonai
*/

.emph {
    text-emphasis-style: dot ;
    -webkit-text-emphasis-style: dot ;
}

/* For FireFox */
@-moz-document url-prefix() {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
    font-style: normal;
    font-weight: normal;
    background-image: url(http://www.scp-wiki.net/local--files/component%3Atheme/dot.png), none;
    background-repeat: repeat-x;
    padding: 0.5em 0 0;
    background-color:transparent;
    background-clip: padding-box, content-box;
    background-size: 1em 1.3em, auto;
}
}

/* For IE10 */
@media screen and (-ms-high-contrast: active), (-ms-high-contrast: none) {
.emph {
    /* For the environments which comply with CSS3. */
    font-family: monospace;
    font-style: normal;
    font-weight: normal;
    background-image: url(http://www.scp-wiki.net/local--files/component%3Atheme/dot.png), none;
    background-repeat: repeat-x;
    padding: 0.5em 0 0;
    background-color:transparent;
    background-clip: padding-box, content-box;
    background-size: 1em 1.3em, auto;
}
}

/* viewport */

@viewport {
    width: device-width;
    zoom: 1.0;
}

/* IE viewport */
@-ms-viewport {
    width: device-width;
    zoom: 1.0;
}

/* opera viewport */
@-o-viewport {
    width: device-width;
    zoom: 1.0;
}

/* chrome viewport - maybe it isn't work... */
@-webkit-viewport {
    width: device-width;
    zoom: 1.0;
}

/* firefox viewport - maybe it isn't work too... */
@-moz-viewport {
    width: device-width;
    zoom: 1.0;
}

/* webkit scrollbar */
::-webkit-scrollbar
{
    width: 9px;    /* for vertical scrollbars */
    height: 9px; /* for horizontal scrollbars */
    border: solid 1px rgba(0, 0, 0, 0.1);
    border-round: 0.5px;
}

::-webkit-scrollbar-track
{
    background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb
{
    background: rgba(50, 50, 50, 0.3);
}

.page-source {
word-break: break-all;
}

/* Responsive Web Design */
img, embed, video, object, iframe, table {
    max-width: 100%;
}

#page-content div, #page-content div table {
    max-width: 100%;
}

#edit-page-comments {
    width: 100%;
}

/* basic Query for mobile devices */

