[[module CSS]]
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5);float: center}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon}

/* 加个作品块 */
.workdiv {
    --titlecolor: #000;
    color: var(--titlecolor);
    cursor: pointer;
    padding: 2rem;
    padding-left: 0rem;
    position: relative;
    transition: color 2s; 
    z-index: 2;
}

.workdiv:hover {
    --titlecolor: #fff;
    color: var(--titlecolor);
}

.workdiv::before {
    background: var(--image-url);
    position: absolute;
    width: 100%;
    height: 100%;
    content: "";
    background-position: left;
    transition: background 1.5s, rotate 2s, opacity 2s, border-left-color 2s; 
    z-index: -2;    
    border-top-right-radius: 1em;
    border-bottom-right-radius: 1em;
    opacity: 0.1;
    filter: grayscale(0.7);
    border-left: 0.5em #000 solid;
}

.workdiv:hover::before {
    background-position: center;
    opacity: 1;
    border-left: 0.5em #888 solid;
}

.workdiv2 {
    margin: 1rem;
    color:var(--titlecolor);
    border-bottom: var(--titlecolor) 0.1em solid;
    transition: color 2s, border-bottom-color 2s;
}

.workdiv2 a{
    color:var(--titlecolor) !important;
    transition: color 2s; 
}

.workdiv3 {
    margin: 1rem;
}

.workdiv3 {
    margin: 1rem;
}

.workdiv4 {
    margin-bottom: -4rem;
    text-align: right;
    display: flex;
    justify-content: flex-end;
    flex-wrap: nowrap;
    flex-direction: column-reverse;
    font-size: 80%;
}

.workdiv4 a{
    color:var(--titlecolor);
    transition: color 2s; 
}

#page-title {
    display:none;
}
[[/module]]
