[[module CSS]]

/* 原西加斯坦代码。个人对页面调整的代码见底部 */

#page-content {
  font-family: sans-serif;
}


@media (max-width: 767px) {
  #main-content {
    margin: 0 2em;
  }
  #page-content {
    font-size: 115%;
  }
}

#page-content .top-wrapper {
  float: left;
  width: 60%;
}

@media (max-width: 1000px) {
  #page-content .top-wrapper {
    float: none;
    width: 100%;
  }
}

#page-content .top-wrapper #toc {
  width: 20rem;
}

@media (max-width: 767px) {
  #page-content .top-wrapper #toc {
    width: 75vw;
  }
}

#page-content .top-wrapper #toc .title,
#page-content .top-wrapper #toc #toc-action-bar a {
  font-size: 0px;
  text-align: left;
}

#page-content .top-wrapper #toc .title::after {
  font-size: 18px;
  content: "目次";
  display: inline-block;
}

#page-content .top-wrapper #toc .title::before {
  font-size: 18px;
  content: "\f0c9";
  margin-right: 0.3rem;
  font-family: "FontAwesome";
  display: inline-block;
}

#page-content .details {
  float: right;
  width: 30%;
  padding: 20px;
  border: solid 1px #708090;
  background: #F5FFFA;
  text-align: center;
}

@media (max-width: 1000px) {
  #page-content .details {
    float: none;
    width: 80%;
    margin: 0 auto;
  }
  #page-content .details img {
    max-width: 400px;
    width: 90%;
  }
}

@media (max-width: 767px) {
  #page-content .scp-image-block {
    clear: both;
    float: none;
    margin-right: auto;
    margin-left: auto;
  }
}

#page-content .bottom-wrapper {
  width: 100%;
  float: left;
}

