[[module CSS]]
a.create-button {
  display: inline-block;
  margin: 0.5em 0;
  padding: 0.625rem;
  text-decoration: none;
  color: #333;
}
span.option-button,
a.option-button {
  display: inline-block;
  margin: 0 0.3125rem;
  padding: 0.125rem 0.625rem;
  border-radius: 0.3125rem;
  text-decoration: none;
  background-color: #633;
  color: #fff;
  box-shadow: 0.0625rem 0.0625rem 0.1875rem rgba(0,0,0,.5);
  cursor: pointer;
}
span.option-button:hover,
a.option-button:hover {
  color: #3498db;
}
.option-button.fa::before {
  margin-right: 0.3125rem;
}
