[[module CSS]]

#page-content::before {
display: none;
}

a.collapsible-block-link {
font-weight: bold;
}

a.collapsible-block-link::hover {
color: slategrey;
}

#page-content > h1, h2, h3, h4, h5, h6 {
color: rgb(34, 34, 0);
}

@keyframes blinky {

0% {
color: red;
}

50% {
color: rgb(34, 34, 0);
}

100% {
color: red;
}

}

.blinktext {
font-weight: bold;
text-align: center;
animation-name: blinky;
animation-duration: 1s;
animation-iteration-count: infinite;
}

[[/module]]
