[[module css]]
.page-rate-widget-box .rate-points{
background-color:#68FF38 !important;
border:solid 1px #68FF38;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown{
border-top:solid 1px #68FF38;
border-bottom:solid 1px #68FF38;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown{
border-top:solid 1px #68FF38;
border-bottom:solid 1px #68FF38;
}

.page-rate-widget-box .cancel{
background-color:#68FF38;
border:solid 1px #68FF38;
}

.page-rate-widget-box .rate-points {
  color: #222222;
}

.rateup.btn.btn-default, .ratedown.btn.btn-default {
  background: white;
}

.rateup.btn.btn-default a, .ratedown.btn.btn-default a {
  color: #222222;
}

#header {
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/silvericeartcollection/linkAge.png);
}

#container-wrap {
  background: #29402E !important;
  color: #68FF38 !important;
}

#page-title, .page-watch-options a, #page-options-bottom a, #page-options-bottom-2 a, .page-tags span a {
  color: #68FF38 !important;
}

#side-bar .side-block, #side-bar .heading {
  border-color: #68FF38;
  background-color: #222222 !important;
}

#side-bar div.menu-item > .image {
  filter: hue-rotate(90deg) brightness(4);
}

#side-bar .side-block a, #side-bar .heading {
  color: #68FF38 !important;
  font-family: monospace;
}

#side-bar .side-block::before, #side-bar .side-block::after {
  background-color: #68FF38;
  font-family: monospace;
}

.under-curtain::before, .outer-space::before {
  content: "";
  display: block;
  width: 300px;
  height: 300px;
  background-size: contain;
  background-repeat: no-repeat;
}

.under-curtain::before {
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/silvericeartcollection/m1.gif);
}

.under-curtain .collapsible-block-folded .collapsible-block-link::before, .outer-space .collapsible-block-folded .collapsible-block-link::before {
  content:"";
  display: inline-block;
  width: 58px;
  height: 58px;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/silvericeartcollection/t.png);
  vertical-align: bottom;
  margin-right: 10px;
  transform: rotate(-90deg);
}

.under-curtain .collapsible-block-unfolded .collapsible-block-link::before, .outer-space .collapsible-block-unfolded .collapsible-block-link::before {
  content:"";
  display: inline-block;
  width: 58px;
  height: 58px;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/silvericeartcollection/t.png);
  vertical-align: bottom;
  margin-right: 10px;
  transform: rotate(0deg);
}

.outer-space::before {
  background-image: url(http://scpsandboxcn.wikidot.com/local--files/silvericeartcollection/m2.gif);
}

.under-curtain, .outer-space {
  display: flex;
  flex-direction: column;
  align-items: center;
}

#main-content .collapsible-block-link {
  font-size: 48px !important;
  color:  #68FF38 !important;
}

.yui-content {
  background: linear-gradient(0deg, black, transparent) !important;
  border: 2px #68FF38 dashed !important;
  box-sizing: border-box;
  padding: 20px !important;
  min-width: 550px;
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.yui-nav li {
  white-space: nowrap;
}

.yui-navset.yui-navset-top {
  display: flex;
  margin-top: 20px;
}

.yui-nav {
  display: flex;
  flex-direction: column;
  font-family: monospace;
  border: none !important;
}

.yui-nav li a {
  background: #222222 !important;
  border-color: #68FF38 !important;
  color: #68FF38 !important;
  width: 100%;
}

.yui-content a {
  color: #ff8400 !important;
}

.yui-content a::hover {
  text-decoration: underline !important;
}

.yui-nav li.selected a {
  background: #68FF38 !important;
  color: #222222 !important;
}

.yui-nav li a em {
  border-color: #68FF38 !important;
}

.yui-content img {
  padding-bottom: 10px;
}

@media screen and (max-width: 1000px) {
  .yui-content {
    min-width: 0px;
  }
}
[[/module]]

