}
.map__room--a4 {
  --room-top: 20.6666666666667%;
  --room-left: 77%;
  --room-height: 12.6666666666667%;
  --room-width: 9%;
  flex-direction: column;
}
.map__room--a2 {
  --room-top: 4.66666666666667%;
