.map__room--a1 {
  --room-top: 4.66666666666667%;
  --room-left: 64.5%;
  --room-height: 17.3333333333333%;
  --room-width: 9%;
  flex-direction: column;
}
.map__room--server-corridor {
  --room-top: 34%;
  --room-left: 24%;
