  --room-width: 25.5%;
}
.map__room--ring-west {
  --room-top: 34%;
  --room-left: 54.5%;
  --room-height: 37.3333333333333%;
  --room-width: 2.5%;
}

.room-icon {
  background-size: 1rem;
  background-position: center;
  background-repeat: no-repeat;
  height: 1.2rem;
  width: 1.2rem;
  display: inline-block;
  font-size: 0px;
  transform: translateX(0.2rem);
  vertical-align: top;
}
.room-icon--new {
  background-image: var(--icon-new);
  background-size: cover;
  border-radius: 99rem;
}
.room-icon--cctv {
  background-image: var(--icon-cctv);
}
.room-icon--mic {
  background-image: var(--icon-mic);
}

.footage__selectors {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  gap: 0.5rem;
}
.footage__selector, .footage__selector:visited {
  position: relative;
  padding: 0.3rem clamp(0.25rem, 2cqw, 0.75rem);
  border: 2px solid var(--swatch-gba-dark);
  border-radius: 0.2rem;
  color: black;
  background-color: var(--swatch-gba-hpale);
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 0.5ch;
  --shadow-colour: var(--swatch-gba-medium);
  --shadow-3d-height: 0.2rem;
  --_shadow-3d-height: 0rem;
  box-shadow: 0px var(--_shadow-3d-height) var(--shadow-colour);
  transform: translateY(calc(-1 * (var(--_shadow-3d-height))));
  transition: box-shadow 0.2s ease, transform 0.2s ease, color 0.1s ease, background-color 0.1s ease, border-color 0.1s ease !important;
}
.footage__selector:hover {
  --_shadow-3d-height: 0.2rem;
  text-decoration: none;
  color: black !important;
  background-color: white;
}

.footage__display {
  display: none;
  position: relative;
  margin: 1.5rem 0;
  --document-padding-inline: 1.5rem;
  padding: 1rem var(--document-padding-inline);
  border: 0.2rem solid var(--swatch-gba-medium);
  border-radius: 0.4rem;
  background-image: linear-gradient(var(--swatch-gba-hpale), transparent 99rem);
  animation: footage-display-fade 0.2s linear both;
}
@keyframes footage-display-fade {
  0% { opacity: 0.5 }
  100% { opacity: 1 }
}
@media (max-width: 700px) {
  .footage__display { --document-padding-inline: 0.75rem }
}
.footage__display h2 {
  font-family: inherit;
  letter-spacing: inherit;
  font-weight: 650;
  margin-top: 1rem;
  margin-bottom: 1.5rem;
  font-size: 1.5rem;
}
.footage__display p {
  margin-right: -0.5ch;
}
.footage__display p:first-of-type:last-of-type {
  font-style: italic;
  opacity: 0.7;
}
.footage__display a[href^='#u-'] {
  display: inline-block;
  vertical-align: 0;
  background-color: var(--swatch-gba-hpale);
  color: black;
  text-decoration: underline 3px var(--swatch-gba-pale);
  filter: brightness(1.05);
}
.footage__display a[href^='#u-']:hover {
  background-color: white;
  color: var(--swatch-gba-dark);
}
.footage__display-start, .footage__display-end {
  display: block;
  text-align: center;
}
:is(.footage__display-start, .footage__display-end)::before {
  font-weight: bold;
  opacity: 0.5;
}
.footage__display:not(:has(p:nth-of-type(2))) :is(.footage__display-start, .footage__display-end) {
  display: none;
}

.footage__instruct {
  font-weight: bold;
  --wght: 700;
  color: var(--swatch-gba-dark);
  font-size: large;
  text-align: center;
}
:root:has(.targets :target) .footage__instruct--below {
  display: none;
}

.footage__feed {
  text-transform: uppercase;
  font-weight: 600;
  color: #000c;
  font-size: 0.85rem;
  letter-spacing: 0.02rem;
  background: #0001;
  border: thin solid var(--swatch-gba-light);
  border-radius: 0.3rem;
  padding: 0.3rem 0.7rem 0.3rem 0.3rem;
  display: inline-block;
  margin-block: -0.5rem 1rem;
}
.footage__feed .room-icon {
  margin-right: 0.3rem;
}

.archive {
  display: flex;
  flex-direction: column;
  border: thin solid rgb(var(--tables-border));
}
.archive p {
  margin: 0;
}
.archive__heading {
  margin: 0;
  background: rgb(var(--tables-header-bg));
  color: rgb(var(--tables-header-txt));
  text-align: center;
  padding: 0.5em 1em;
}
.archive .list-pages-box {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(4em, max-content));
  gap: 0.5em;
  padding: 0.5em;
  justify-content: center;
}
.archive .list-pages-item {
  position: relative;
  height: 4em;
}
.archive .list-pages-item:is(:nth-child(13), :nth-child(14)) {
  grid-column: span 3;
}
.archive .list-pages-item:is(:nth-child(13), :nth-child(14), :nth-child(15)) a {
  font-size: 0;
}
.archive .list-pages-item:nth-child(13) a::after {
  content: "RTF-K13事件报告";
  font-size: var(--base-font-size);
}
.archive .list-pages-item:nth-child(14) a::after {
  content: "行动后事件报告";
  font-size: var(--base-font-size);
}
.archive .list-pages-item:nth-child(15) a::after {
  content: "███";
  font-size: var(--base-font-size);
}
.archive .list-pages-item a {
  position: absolute;
  inset: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  color: rgb(var(--link-color));
  border: thin solid rgb(var(--tables-border));
  text-decoration: none;
}
.archive .list-pages-item a:hover {
  color: rgb(var(--hover-link-color));
  border-color: rgb(var(--hover-link-color));
  text-decoration: none;
}
.archive .list-pages-item::before, .archive .list-pages-item::after {
  position: absolute;
  inset: 0;
  height: 25%;
  text-align: center;
  line-height: 125%;
  font-weight: bold;
  font-size: 75%;
}
.archive .list-pages-item:last-child::before { /* todo this will break when the 2nd day starts */
  content: "最后记录";
  color: rgba(0, 0, 0, 0.5);
}

.footage .yui-nav {
  clip-path: none;
  justify-content: center;
}
.footage .yui-nav * {
  display: contents !important;
}
.footage .yui-nav em {
  display: inline-block !important;
  color: black !important;
  text-decoration: underline;
  text-decoration-style: dotted;
  text-underline-offset: 0.25rem;
  border: none !important;
}
.footage .yui-nav .selected em {
  font-weight: 900;
  text-decoration: none;
}
.footage .yui-nav li a::before { display: none; }
.footage .yui-content {
  border: thin dashed rgb(var(--tabs-content-border-color));
  border-color: rgb(var(--tabs-content-border-color)) rgba(var(--tabs-content-border-color), 0.25);
  padding: 0 1rem;
}

.retinal-scan {
  display: flex !important;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  gap: 0.5rem 2rem;
  border: medium solid rgb(var(--swatch-primary-darker)) !important;
  background-color: rgb(240, 240, 240) !important;
  box-shadow: 0px 0.2rem 1rem rgba(0, 0, 0, 0.05) !important;
}
.retinal-scan p { margin: 0 }
.retinal-scan :nth-child(1) {
  font-weight: bold;
}

/* Let something take control of navigation */
:root:has(.footage__display-override) :is(.footage__selectors, .footage__instruct) {
  display: none;
}
:root:has(.footage__display-override) .footage__display > p:first-of-type:last-of-type {
  font-style: initial;
  opacity: initial;
}
:root:has(.footage__display-override) .map__room {
  background-color: transparent !important;
  border: none !important;
  pointer-events: none;
  color: black !important;
  content: none !important;
}
:root:has(.footage__display-override) .map {
  filter: grayscale(1) brightness(1.2) contrast(1.2);
}

:root:has(.footage__display-override--max) :is(.map, .time-context) {
  display: none;
}

/* Map/footage hover links */
:root:has([href='#u-pantry']:hover) .map__room--pantry,
:root:has([href='#u-storage']:hover) .map__room--storage,
:root:has([href='#u-foyer']:hover) .map__room--foyer,
:root:has([href='#u-hangar']:hover) .map__room--hangar,
