[[module CSS]]
/* --- 翻译修改 --- */
h2 {
    font-weight:bold;
}

/* ---- VARIABLES ---- */
:root{
    /* Tabview Content Background */
     --very-pale-blue: hsl(205, 100%, 97%);
    /* [SELECTED] */
     --dark-blue: hsl(205, 44%, 48%);
    /* [HOVER] */
     --dark-moderate-blue: hsl(205, 49%, 32%);

    /* Border Type 1 */
     --dark-grayish-blue: hsl(205, 3%, 52%);
    /* Border Type 2 */
     --very-dark-blue: hsl(205, 94%, 20%);

    /* [SELECTED] + [HOVER] text color */
     --very-light-grey: hsl(0, 0%, 95%);
    /* [UNSELECTED] text color */
     --very-dark-grey: hsl(0, 0%, 05%);
}

/* ---- HEADER ---- */
 #page-title{
     display: none;
}
 .page-rate-widget-box{
     margin-right: 0;
}
 a, body, span{
     word-break: unset;
}

/* ---- FEATURES | [LISTPAGES] ---- */
 .list-pages-item p{
     margin-block-start: 0 !important;
}

/* ---- FEATURES | [TOP ROW] ---- */
 .feature-block{
     margin: 1rem auto;
     display: flex;
     flex-wrap: wrap;
     grid-gap: 1.4rem;
     align-items: stretch;
}
 .content-panel{
     border: solid 1px var(--dark-grayish-blue);
     background-color: var(--dark-blue);
}
.content-panel .panel-body{
    background: var(--very-pale-blue) url(http://www.scp-wiki.net/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
}
 .content-panel .panel-footer{
     color: var(--very-pale-blue);
}
 .feature-block .content-panel{
     flex-basis: 17.5rem;
     flex-grow: 1;
     margin: 0 auto;
     display: flex;
     flex-direction: column;
}
 .feature-block .content-panel .panel-body{
    /* To make the content height take up all available space */
     flex-grow: 1;
     padding: .125rem 1rem;
     overflow-y: hidden;
}
 .feature-block .feature-title{
     margin: .5em 0 .4em;
     padding: 0;
     letter-spacing: 1px;
     font-size: 120%;
     font-weight: bold;
}
 .feature-block .feature-subtitle{
     font-size: 90%;
     margin-top: -1em;
     display: block;
}

/* ---- FEATURES | [SECOND ROW] ---- */
 .news-block{
     margin: 1.5rem auto;
}
 .news-block .panel-body{
     padding: 0;
}
 .news-block .panel-body .alternate{
     padding: 5px 10px;
}
 .news-block .news-title{
     font-weight: bold;
     font-size: 110%;
     margin-bottom: 5px;
     color: hsl(205, 33%, 30%);
}
 .news-block .news-content{
     margin: 5px 0;
}
 .news-block .news-title > p,
 .news-block .news-content > p{
     margin: 0 !important;
     margin-block-start: 0 !important;
     margin-block-end: 0 !important;
}

/* ---- YUI TAB BASE ---- */
 .yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a{background-color:inherit;background-image:inherit}.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus{background:inherit;text-decoration:inherit}.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:hover{color:inherit;background:inherit}.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav{border-color:inherit}.yui-navset li{line-height:inherit}

/* ---- YUI TAB CUSTOMIZATION ----
 * Adapted From 'Black Highlighter Theme'
**/
 .yui-navset *{
     transition:
          color 80ms cubic-bezier(.4, 0, .2, 1),
          background-color 80ms cubic-bezier(.4, 0, .2, 1);
}
 .yui-navset .yui-nav,
 .yui-navset .yui-navset-top .yui-nav{
     display: flex;
     flex-wrap: wrap;
     width: calc(100% - .125rem);
     margin: 0 auto;
     border-color: var(--dark-blue);
     box-shadow: 0 calc(.0625rem * 5) 0 0 var(--very-dark-blue);
}
 .yui-navset .yui-nav li a,
 .yui-navset-top .yui-nav li a,
 .yui-navset-bottom .yui-nav li a{
     display: flex;
     align-items: center;
     justify-content: center;
     width: 100%;
}
 .yui-navset .yui-nav a,
/* ---- Link Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav a{
     color: var(--very-dark-grey);
    /* ---- Tab Background Colour | [UNSELECTED] ---- */
     background-color: var(--very-light-grey);
    /* -------- */
     background-image: none;
     border: unset;
}
 .yui-navset .yui-nav a:hover,
 .yui-navset .yui-nav a:focus{
     color: var(--very-light-grey);
    /* ---- Tab Background Colour | [HOVER] ---- */
     background: var(--dark-moderate-blue);
    /* -------- */
}
 .yui-navset .yui-nav li,
/* ---- Listitem Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav li{
     position: relative;
     display: flex;
     flex-grow: 2;
     max-width: 100%;
     margin: 0;
     padding: 0;
     background-color: var(--dark-blue);
     box-shadow: 0 0 0 .0625rem var(--very-dark-blue);
}
 .yui-navset .yui-nav li em{
     border: unset;
}
 .yui-navset .yui-nav a em,
 .yui-navset .yui-navset-top .yui-nav a em{
     padding: .35em .75em;
     text-overflow: ellipsis;
     overflow: hidden;
     white-space: nowrap;
}
 .yui-navset .yui-nav .selected,
/* ---- Selection Modifier ---- */
 .yui-navset .yui-navset-top .yui-nav .selected{
     flex-grow: 2;
     margin: 0;
     padding: 0;
     color: var(--very-light-grey);
    /* ---- Tab Background Colour | [SELECTED] ---- */
     background: var(--dark-blue);
    /* -------- */
}
 .yui-navset .yui-nav .selected a,
 .yui-navset .yui-nav .selected a em{
     border: var(--very-dark-blue);
}
 .yui-navset .yui-nav .selected a{
     width: 100%;
     color: var(--very-light-grey) !important;
     background-image: none;
}
 .yui-navset .yui-nav .selected a:focus,
 .yui-navset .yui-nav .selected a:active{
     color: var(--very-light-grey);
     background-color: var(--dark-blue);
}
 .yui-navset .yui-nav .selected a:hover{
     cursor: default;
}
 .yui-navset .yui-content{
    /* ---- Content Background ---- */
     background-color: var(--very-pale-blue);
    /* -------- */
}
 .yui-navset .yui-content,
 .yui-navset .yui-navset-top .yui-content{
     padding: .5em;
     border-color: var(--dark-grayish-blue);
}
[[/module]]

