[[module CSS]]

div#container-wrap {
 background: url(https://ifh.cc/g/bhaoWO.png) top left repeat-x;   
}

header{ background: #5e4978; }
 h1, #page-title {
     color: #5e4978;
}

#search-top-box-input {
    background-color: #0066bc;
}

#search-top-box-input:hover, #search-top-box-input:focus {
    background-color: #5e4978;
}

#search-top-box-form input[type=submit] {
    background-color: #037ade;
    background: linear-gradient(to bottom, #84b7e2,#524978);
}

#top-bar ul li .sfhover a, #top-bar ul li:hover a{ color : #5e4978;}

.page-rate-widget-box .rate-points {
    background-color: #5e4978 !important;
    border: solid 1px #5e4978;
}
.page-rate-widget-box .cancel {
    background-color: #5e4978;
    border: solid 1px #5e4978;
}

.page-rate-widget-box .cancel a {
    color: #F7FFFF;
}

.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #F7FFFF;
    border-top: solid 1px #5e4978;
    border-bottom: solid 1px #5e4978;
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    color: #5e4978;
}

@media (max-width: 767px){
#side-bar {
    background-color: rgb(94, 73, 120);
}
}

#side-bar .heading {
    color: #796186;
    border-bottom: solid 1px #ad8abf;
}

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #5e4978;
    box-shadow: 0 2px 6px rgba(60,0,103,.5);
    background: #fff;
}

#side-bar a:visited {
    color: #456086;
}

a {
    color: #796186;
}


[[/module]]


