[[module CSS]]

#main-content {
min-width:50rem;
}

div#header h1 a::before {
    content: "大正佰伍拾年";
}

#page-content p{
        max-height: 80vh;
        float:right;
         letter-spacing: 0.3em;
             margin-left: 0em;
         font-size:1.6rem;
}

@media (min-width: 700px) {
#page-title,
#page-content p{
    writing-mode: horizontal-tb;
    direction: ltr;
}

#page-content a.footnoteref {
    display: inline-block;
    width: 0;
    height: 0.2rem;
    font-size: 0;
    text-decoration: none;
}

#page-content a.footnoteref::before {
  font-size: 0.8rem;
    content: '㊀';
}
}


div#header h1 a::before,
#page-content tt,
#page-title,
#page-content p {
    font-family:  'Huiwen-mincho', '华文中宋', '思源宋体 CN' , lisu;
}

#page-content p {
    writing-mode: vertical-rl;
    writing-mode: tb-lr;
margin:1em 0em 0em 0em;
    line-height: 1.3em;
}

#page-title {
    writing-mode: horizontal-tb;
    direction: rtl;
    margin: inherit;
    unicode-bidi: bidi-override;
    text-align: center;
    font-size: 260%;
}

#page-content tt{
    font-size:140% !important;
    margin: 1em 1em 0 0.5em;
    display: block;
    width: 100%;

}

.mob-hid p{
    width: 100%;
     line-height: 10rem;
}

/* 移动端 */
@media (max-width: 500px) {
#page-title,
#page-content p{
    writing-mode: horizontal-tb;
    direction: ltr;
}

#page-content p{
        max-height: auto;
        float:none;
        margin-bottom:0em;  
         font-size:1.3rem;
    text-align: center;
    margin:0em 0em 0.5em 0em;

}

#page-content tt{   
    margin:1em 0em 0em 0em;

}

#main-content {
min-width:auto;
}

    .mob-hid{
   display: none;
}

#page-content  p{
    animation: sss 3s  forwards;
    background-size: 0% 100%;
}
}



#u-ratingboxhid {   
 opacity:0.2;  transition: all 0.5s ease-out;
}
#u-ratingboxhid:hover {  
 opacity:1;
}

#page-content  p{
        background-image: linear-gradient(0deg, #333 100%, transparent);        
        -webkit-background-clip: text;
        color: transparent;
    white-space: nowrap;
    animation: w 3s  forwards;
    overflow : hidden;
    background-clip: text;
    -webkit-text-fill-color:transparent;
    background-size: 100% 0%;
    background-repeat:  no-repeat;
}

@media (max-width: 500px) {    
#page-content  p{
    animation: sss 3s  forwards;
    background-size: 0% 100%;
}
}

@keyframes w{
    0% {
                     background-size: 100% 0%;
    }
    100% {
                     background-size:100% 100%;
    }
}

@keyframes sss{
    0% {
                     background-size: 0% 100%;
    }
    100% {
                     background-size:100% 100%;
    }
}




:root {
  --fade-in-delay: 2s;
}

#page-content  :nth-child(3)  { animation-delay: calc(1 * var(--fade-in-delay)); }
#page-content  :nth-child(4) { animation-delay: calc(2 * var(--fade-in-delay)); }
#page-content  :nth-child(5) { animation-delay: calc(3 * var(--fade-in-delay)); }
#page-content  :nth-child(6) { animation-delay: calc(4 * var(--fade-in-delay)); }
#page-content  :nth-child(n+7) { animation-delay: calc(6 * var(--fade-in-delay)); }

#breadcrumbs {
    display: none;
}

[[/module]]
