[[module CSS]]
.content-panel .content-type-title > h3 {
  margin: 0;
}
.content-panel .content-type-title h3 {
  font-size: 120%;
  font-weight: bold;
  padding: 5px 20px;
  background-color: #666;
  color: #fff;
  border-radius: 8px 8px 0 0;
  box-shadow: inset 0 1px 1px rgba(255,255,255,.8),
    inset 0 15px 1px rgba(255,255,255,.2),
    inset 0 15px 10px rgba(255,255,255,.2);
}
.content-panel .content-type-footer > p {
  margin: 0;
}
.content-panel .content-type-footer p {
  font-size: 150%;
  text-align: center;
  font-weight: bold;
  padding: 5px 20px;
  background-color: #DCDCDC;
  color: #000;
  border-radius: 0 0 8px 8px;
  box-shadow: inset 0 1px 1px rgba(255,255,255,.8),
    inset 0 15px 1px rgba(255,255,255,.2),
    inset 0 15px 10px rgba(255,255,255,.2);
}

.content-panel .content-type-footer p a {
margin-right: 0.5em;
}

.content-panel .content-type-options{
  padding: 0 20px;
}
.content-panel .content-type-options ul {
  padding-left: 2em;
  margin-left: 0;
}
[[/module]]

