[[module CSS]]
/*
  Derived from CSS Theme for Toheisha
  --author: RTa_technology
  --source: http://rtas.wikidot.com/n-theme:toheisha
  --license: CC BY-SA 3.0 ( http://creativecommons.org/licenses/by-sa/3.0 )
*/
/*==============================*/
:root {
  --blue-accent: 50, 50, 80;
  --beige-accent: 255, 255, 255;
  --light-blue-accent: 171, 191, 239;
  --vivid-blue-accent: 64, 102, 148;
  --content-bg-color: 255, 255, 255;
}
::selection {
  background: #243151;
  background: rgb(var(--blue-accent));
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
}
/* yui-nav decoration*/

/*==============================*/

.yui-navset * {
  -webkit-transition: background-color 80ms cubic-bezier(.4, 0, .2, 1), color 80ms cubic-bezier(.4, 0, .2, 1);
  transition: background-color 80ms cubic-bezier(.4, 0, .2, 1), color 80ms cubic-bezier(.4, 0, .2, 1);
}
.yui-navset {
  position: relative;
  clear: both;
}
.yui-navset:before {
  position: absolute;
  z-index: -1;
  width: 100%;
  height: 100%;
  background-color: #ebe0ce;
  background-color: rgb(var(--beige-accent));
  content: "";
}
#page-content .yui-navset .yui-nav, #page-content .yui-navset .yui-navset-top .yui-nav {
  display: -webkit-box;
  display: flex;
  -webkit-flex-wrap: wrap;
  flex-wrap: wrap;
  margin: 0 auto;
  width: calc(100% - .125rem);
  border: 0;
  box-shadow: 0 .3125rem 0 0 #fff;
  zoom: unset;
}
#page-content .yui-navset .yui-nav a, #page-content .yui-navset .yui-navset-top .yui-nav a {
  border: unset;
  background-color: #ebe0ce;
  background-color: rgb(var(--beige-accent));
  background-image: none;
  color: #243151;
  color: rgb(var(--blue-accent));
  zoom: unset;
}
#page-content .yui-navset .yui-nav a:focus, #page-content .yui-navset .yui-nav a:hover {
  background: none;
  background-color: #243151;
  background-color: rgb(var(--blue-accent));
  color: #ebe0ce;
  color: rgb(var(--beige-accent));
  zoom: unset;
}
#page-content .yui-navset .yui-nav li, #page-content .yui-navset .yui-navset-top .yui-nav li {
  position: relative;
  display: -webkit-box;
  display: flex;
  -webkit-flex-grow: 2;
  flex-grow: 2;
  margin: 0;
  padding: 0;
  max-width: 100%;
  border-color: rgba(0, 0, 0, 0);
  background-color: #ebe0ce;
  background-color: rgb(var(--beige-accent));
  box-shadow: 0 0 0 0.0625rem #243151;
  box-shadow: 0 0 0 0.0625rem rgb(var(--blue-accent));
  zoom: unset;
  -webkit-box-flex: 2;
}
#page-content .yui-navset-bottom .yui-nav li a, #page-content .yui-navset-top .yui-nav li a, #page-content .yui-navset .yui-nav li a {
  display: -webkit-box;
  display: flex;
  -webkit-align-items: center;
  align-items: center;
  -webkit-justify-content: center;
  justify-content: center;
  width: 100%;
  vertical-align: bottom;
  zoom: unset;
  -webkit-box-align: center;
  -webkit-box-pack: center;
}
#page-content .yui-navset .yui-nav a em, #page-content .yui-navset .yui-navset-top .yui-nav a em {
  overflow: hidden;
  padding: .35em .75em;
  text-overflow: ellipsis;
  white-space: nowrap;
}
#page-content .yui-navset .yui-nav li em {
  border: unset!important;
}
#page-content .yui-navset .yui-nav .selected, #page-content .yui-navset .yui-navset-top .yui-nav .selected {
  -webkit-flex-grow: 2;
  flex-grow: 2;
  margin: 0;
  padding: 0;
  background-color: #243151;
  background-color: rgb(var(--blue-accent));
  -webkit-box-flex: 2;
}
#page-content .yui-navset .yui-nav .selected a, #page-content .yui-navset .yui-nav .selected a em {
  border: rgba(0, 0, 0, 0);
}
#page-content .yui-navset .yui-nav .selected a {
  width: 100%;
  background: none;
  color: #ebe0ce!important;
  color: rgb(var(--beige-accent))!important;
}
#page-content .yui-navset .yui-nav .selected a:active, #page-content .yui-navset .yui-nav .selected a:focus {
  background: none;
  background-color: #243151;
  background-color: rgb(var(--blue-accent));
  color: #fff;
}
#page-content .yui-navset .yui-nav .selected a:hover {
  background: none;
  cursor: default;
}
#page-content .yui-navset .yui-content {
  position: relative;
  margin-bottom: 20px;
  background-color: transparent;
  color: #243151;
  color: rgb(var(--blue-accent));
}
#page-content .yui-navset .yui-content, #page-content .yui-navset .yui-navset-top .yui-content {
  border-color: #243151;
  border-color: rgb(var(--blue-accent));
  border-top: #243151 solid 5px;
  border-top: rgb(var(--blue-accent)) solid 5px;
}
#page-content .yui-navset-left .yui-content {
  border: none;
}
#page-content .yui-navset .yui-content:before {
  position: absolute;
  top: 3px;
  left: 0;
  z-index: -1;
  width: 100%;
  height: calc(100% - 6px);
  outline: #243151 solid 1px;
  outline: rgb(var(--blue-accent)) solid 1px;
  content: '';
}
#page-content .yui-navset .yui-content:after {
  position: absolute;
  top: 0;
  left: 3px;
  z-index: -1;
  width: calc(100% - 6px);
  height: 100%;
  outline: #243151 solid 1px;
  outline: rgb(var(--blue-accent)) solid 1px;
  content: '';
}
[[/module]]
