[[module CSS]]
#page-title {
  display: none;
}

.cromplug {
    background: #f5e5e8;
    color: #9f3c4e;
    width: fit-content;
    text-align: center;
    margin-top: 0.5rem;
    border-radius: 0.5rem;
    padding: 0.75rem;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/search%3Acrom/crom.png);
    background-size: cover;
    background-blend-mode: overlay;
    background-position: center 15%;
}
.cromplug p {
    margin: 0;
}
.cromplug a, .cromplug a:visited {
    color: #9f3c4e;
    font-weight: bold;
}
[[/module]] 

