.new-side {
    position: fixed;
    top: 0%;
    left: 0%;
    width: 266px;
    box-sizing: border-box;
    height: 100%;
    min-height: 500px;
    z-index: 20;
    background: var(--base-color);
    overflow-y: auto;
    transition: 0.5s;
    transform: translateX(-266px);
}

.new-side:target {
    transform: translateX(0px);
}

.side-content {
    text-align: center;
    margin-top: 5em;
}

.side-content p {
    margin: 0 !Important;
}

.side-content,
.side-content a,
.side-content span {
    color: var(--background);
    text-decoration: none !Important;
    font-size: 14px;
    font-weight: bold;
    line-height: 30px;
    overflow-x: hidden;
}

.open-side {
    color: var(--background) !important;
    display: block;
    position: fixed;
    top: 0%;
    left: 0%;
    font-size: 2.1em;
    height: 61px;
    width: 61px;
    font-weight: bold;
    background: var(--base-color) !Important;
    z-index: 12;
    text-align: center;
}

.open-side>* {
    display: inline-block;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding-top: 2px;
}

.open-side>span>span {
    font-size: 9px;
    font-weight: normal;
}

#content-wrap {
    position: relative;
    margin: 4.8em auto 0;
}

.side-outside {
    transition: 0.5s;
    max-height: 30px;
    overflow: hidden;
}

.side-outside:hover {
    max-height: 100px;
}

.side-outside a,
.side-outside span {
    transition: .3s;
}

.side-outside:hover span {
    color: rgba(255, 255, 255, .7) !Important;
}

.side-outside a:hover {
    color: rgba(255, 255, 255, .7) !Important;
}

.side-content a {
    transition: .3s !important;
}

.side-content a:hover {
    color: rgba(255, 255, 255, .7) !Important;
}

.side-inside {
    display: block;
    padding: 4px 0;
}

.side-inside a {
    transition: .3s;
}

.side-inside a:hover {
    color: rgba(255, 255, 255, .7);
}

.new-side hr {
    margin: 0.3em auto;
    height: 0.5px;
    width: 10px;
    background-color: rgba(255, 255, 255, .3);
}

.new-side:target .side-content>.side-outside,
.new-side:target .side-content>a,
.new-side:target .side-content>p {
    opacity: 0;
    animation-name: two;
    animation-duration: 2.5s;
    animation-iteration-count: 1;
    animation-fill-mode: both;
    animation-delay: .2s;
}

@keyframes two {
    0% {
        opacity: 0;
        transform: translateX(50px);
    }
    100% {
        opacity: 1;
        transform: translateX(0);
    }
}

.new-side .close-side {
    display: block;
    position: absolute;
    top: 10px;
    right: 5px;
    font-size: 2.5em;
    text-decoration: none;
    color: var(--background);
}

.new-side .close-side:after {
    font-family: FontAwesome;
    content: '\f00d';
}

.side-sites {
    width: 70%;
    display: flex;
    height: 45px;
    border-bottom: 0.5px solid rgba(255, 255, 255, .3);
    border-top: 0.5px solid rgba(255, 255, 255, .3);
    justify-content: space-around;
    align-items: center;
    margin: 5px auto 0 auto;
    position: relative;
}

.side-sites a {
    font-size: 90%;
    transition: .3s;
}

.side-sites a:hover {
    color: rgba(255, 255, 255, .7) !Important;
}

.side-sites * {
    flex-basis: 100%;
    flex-shrink: 1;
    flex-grow: 1;
}

span.center-line {
    display: inline-block;
    font-size: 70%;
    color: rgba(255, 255, 255, .3);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.new-side::-webkit-scrollbar {
    width: 5px;
    height: 9px;
    border: none;
}

.new-side::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, .6);
}

.new-side::-webkit-scrollbar-track {
    background: none;
}

.side-links {
    width: 70%;
    margin: 0 auto;
    display: flex;
    height: 45px;
    justify-content: space-around;
    align-items: center;
    position: relative;
}

.side-links a {
    flex-basis: 100%;
    flex-shrink: 1;
    flex-grow: 1;
