[[include fragment:nav-generic-css]]

[[module PageCalendar category="_default"]]

[[div class="tale-list content-type-description"]]
[[module ListPages category="*" order="date desc" wrapper="no" separate="false" tags="tale" perPage="100" date="@URL|last 12 month"]]

[[div class="tale-block"]]
[[div_ class="index"]]
[[span]]Index No.[[/span]][[span class="numbering"]][[#expr %%total%% - %%index%% + 1]][[/span]]
[[/div]]
[[div_ class="title"]]
%%linked_title%% [[span class="author"]]by %%created_by_linked%%[[/span]]
[[/div]]
[[div_ class="date"]]
%%created_at|%Y %b %e|agohover%%
[[/div]]

[[div_ class="sec-info"]]
[[span class="rating"]]Rating: [[#ifexpr %%rating%% > -1 | + | - ]][[#expr abs(%%rating%%)]][[/span]]
[[span class="comments"]]%%comments%% [[span]]Comments[[/span]]ðŸ’¬ï¸Ž[[/span]]
[[/div]]

