[[module CSS]]
 body{
     --barColour: #b98f0e;
     color: rgb(12, 12, 12);
     word-break: unset;
}
 .info-container .collapsible-block-unfolded{ background: #fff; }
 .translation_block, .u-faq, #u-author_block{ display:none; }

/* HEADER */
 #header h1 a{
     color: transparent;
     font-family: 'Verdana', Arial, sans-serif;
     text-shadow: none;
}
 #header h1 a::before{
     content: "鳥類部門";
     color: #eee;
     text-shadow: 3px 3px 5px rgb(12, 12, 12);
     letter-spacing: -0.02em;
}
 #header h2 span{
     color: transparent;
     text-shadow: none;
}
 #header h2 span::before{
     '我らの巣を取り戻すまで';
     color: #eee;
     text-shadow: 1px 1px 1px rgba(12, 12, 12, .8);
}

/* RATE MODULE */
 div.page-rate-widget-box .rate-points{
     background-color: #b98f0e;
     border: solid 1px #b98f0e;
}
 .page-rate-widget-box .rateup,
 .page-rate-widget-box .ratedown{
     background-color: #fffff0;
     border-top: solid 1px #b98f0e;
     border-bottom: solid 1px #b98f0e;
}
 .page-rate-widget-box .rateup a,
 .page-rate-widget-box .ratedown a{
     color: #b98f0e;
}
 .page-rate-widget-box .rateup a:hover,
 .page-rate-widget-box .ratedown a:hover{
     background: #b98f0e;
}
 .page-rate-widget-box .cancel{
     background-color: #b98f0e;
     border: solid 1px #b98f0e;
}
 .page-rate-widget-box .cancel a{
     color: #664d00;
}
 .page-rate-widget-box .cancel a:hover{
     background: #b98f0e;
     color: #fffff0;
}

/* DIV BLOCKS */
 .header-image{
     border: 3px solid rgb(12, 12, 12); 
     background: #fff;
}
 .showcase{
     border: 3px solid rgb(12, 12, 12); 
     background: #fff; 
     padding: 0 .5em;
}
 .showcase hr{ background: #ffc818; height: 2px; }

/* FORMATTING */
 .bg-gradiant{
     background-color: #fff; 
     background: linear-gradient(to bottom, #f7f7f7, #ffc818, #ffc818, #fff);
     padding: 0 2%; 
}
 hr{
     display: block;
     height: 1px;
     margin: 1em 0;
}
 h1,h2,h3,h4{
     display: flex;
     flex-direction: column;
     align-items: center;
     justify-content: center;

     color: rgb(12, 12, 12);
     word-break: unset;
}

/* TABLES */
 blockquote,
 .article blockquote,
 .continued blockquote{
     padding: 0;
     background: #fff;
     border: solid 1px #ffc818;
}
 .article table.wiki-content-table,
 .continued table.wiki-content-table{
     width: 100%;
     margin: 0 0 -1px;
}
 .article table.wiki-content-table th,
 .continued table.wiki-content-table th{
     padding: 0;
     border: solid 1px #ffc818;
     font-weight: normal;
}
 .article table.wiki-content-table td,
 .continued table.wiki-content-table td{
     border: solid 1px #ffc818;
     border-right: 0;
}
 .continued table.wiki-content-table td{
     border-top: 0;
}
[[/module]]

