[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');

:root {
    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --user-button-width: 2.25rem;
    --side-bar-button-width: 2.25rem;
    --main-content-top-margin: 1.5rem;
    --main-content-width: 52rem;

    --basalt-undertone: 20,202,102;

    --basalt-bright-element-color: 20,255,127;
    --basalt-dark-element-color: 20,144,72;
    --basalt-darker-element-color: 50,202,101;

    --base-font-size: 1.1rem;

    --body-font: "Noto Sans SC", sans-serif;
}

div#content-wrap::before {
    content: "";
    display: block;
    position: static;
    width: auto;
    height: 22rem;
    margin-top: calc(var(--header-final-height) * -1);
    background-image: linear-gradient(to top, rgba(0, 0, 0, 0.6) 0%, transparent 1rem), url("http://ah-sandbox.wikidot.com/local--files/theme:high/head2.jpg");
    background-size: cover;
    background-position: center;
}

div#header h1 a::before {
    margin-right: 0rem;
}

div#header h1 a span::after {
    margin-bottom: 0;
}

div#header {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.4);
    box-shadow: unset;
    backdrop-filter: blur(0.2rem);
}

div#main-content {
    --header-font: "Noto Serif SC", serif;
}

div#page-content {
    line-height: 1.75;
    text-align: justify;
    word-break: break-all;
    justify-align: both;
}

div#page-content p {
    margin-block: 1.75em;
    text-indent: 2em;
}

h1 {
    color: inherit;
    font-family: "Noto Serif SC", "Noto Serif TC", serif;
    font-weight: 900;
    font-size: 1.8em;
    text-align: center;
}

div.blockquote.report {
    border-radius: 0.5rem;
    max-width: 100% !important;
    width: auto;
    color: rgb(var(--basalt-primary-color));
    background-color: rgb(175, 178, 177);
    background-image: url("https://ah-sandbox.wikidot.com/local--files/trap/tech_g.svg");
    background-size: 2.5rem auto;
    background-position: 0.8rem 0.8rem;
    background-repeat: no-repeat;
}

div#page-content div.earthworm * {
    text-indent: 0 !important;
    margin: 0 !important;
}

div#page-content div.earthworm  a {
    margin-inline: 1rem !important;
}

div#page-content div.blockquote.letter p:nth-child(1),
div#page-content div.blockquote.report p {
    text-indent: 0;
}

@media (max-width: 1024px) {
div#content-wrap::before {
    max-height: 40vh;
}
}
[[/module]]


