[[module css]]
body {
    background-color: #AAA;
    color: #323232;
}

div#container-wrap {
    background: url(http://borradores-scp-es-2.wdfiles.com//local--files/yorick1/4) top left repeat-x;
}

/* HEADER */

#header {
    background: url(http://borradores-scp-es-2.wdfiles.com/local--files/yorick1/5) 0px 25px no-repeat;
    background-size: 130px;
}

@media (max-width: 979px) {
    #header {
        background-position: 0;
        background-size: auto 88px;
    }
}

@media (max-width: 768px) {
    #header {
        background-position: 1em 4em;
        background-size: auto 77px;
    }
}

@media (max-width: 580px) {
    #header {
        background-position: -10px 4.5em;
        background-size: auto 75px;
    }
}

@media (max-width: 580px) {
    #header {
        background-position: -10px 4.5em;
        background-size: auto 75px;
    }
}

@media (max-width: 479px) {
    #header {
        background-size: auto 70px;
    }
}

#search-top-box-input {
    border: solid 1px #EDEDED;
    border-radius: 5px;
    color: #F22424;
    background-color: #1C1818;
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .5);
}

@media (max-width: 580px) {
    #search-top-box {
        top: 100px;
    }
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #efeaea;
    color: #fff;
    background-color: #171717;
    box-shadow: inset 1px 1px 3px rgba(0, 0, 0, .8);
}

#search-top-box-form input[type=submit] {
    border: solid 1px #EDEDED;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: #fff;
    background-color: #282828;
    background: linear-gradient(to bottom, #878787, #5b5b5b, #303030);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    cursor: pointer;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: #fff;
    text-shadow: 0 0 1px rgba(255, 255, 255, .25) background-color: #a09a9a;
    background: linear-gradient(to bottom, #d1cfcf, #878787, #5b5a5a);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .8);
}

#login-status {
    color: #DEDEDE;
    font-size: 90%;
    z-index: 30;
}

#login-status a {
    background: transparent;
    color: #fff;
}

#login-status ul a {
    color: white;
    background: #171717;
}

#account-topbutton {
    background: #ccc;
    color: #700;
}

#account-options li a:hover {
    background-color: white;
    color: black;
    border-top: 1px solid #F22424;
    border-bottom: 1px solid #F22424;
}

.printuser img.small {
    margin-right: 1px;
}

#header h1 a {
    font-size: 150%;
    font-family: 'Korinna', serif;
    font-weight: bold;
}

@media (max-width: 768px) {
    #header h1 a {
        font-size: 130%;
    }
}

@media (max-width: 580px) {
    #header h1 a {
        font-size: 100%;
    }
}

#header h1 a span {
    font-size: 0px;
}

#header h1 a::before {
    content: "アンタレス協会";
    color: #000000;
    text-shadow: 1px 1px 2px white, 0 0 1em white, 0 0 0.2em white;
}

#header h2 {
    font-size: 105%;
}

#header h2 span {
    font-size: 0px;
    font-family: 'Korinna', serif;
}

#header h2 span::before {
    content: "黄金時代を目指して";
    color: #000000;
    font-size: 13px;
    text-shadow: 1px 1px 1px #000000;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
}

/* TOP MENU */

#top-bar a {
    color: #fff;
    background: transparent;
}

#top-bar ul li {
    border: 0;
}

#top-bar ul li ul {
    border: solid 1px #2E2E2E;
    box-shadow: 0 2px 6px rgba(0, 0, 0, .5);
    border-top: 0;
}

#top-bar ul li a {
    border-left: solid 1px rgba(64, 64, 64, .1);
    border-right: solid 1px rgba(64, 64, 64, .1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #0D0D0D;
    color: white;
    border-left: solid 1px rgba(64, 64, 64, 1);
    border-right: solid 1px rgba(64, 64, 64, 1);
}

#top-bar ul li a:hover {
    color: black;
    background-color: #FAFAFA;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-width: 0;
    border-top: 1px solid #575757;
    line-height: 160%;
    max-height: none;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #fff;
    text-decoration: none;
}

#top-bar ul li ul {
    border-width: 0 1px 1px 1px;
    width: auto;
}

#top-bar ul li ul li,
#top-bar ul li ul li.sfhover,
#top-bar ul li ul li,
#top-bar ul li ul li:hover {
    border-width: 0;
}

#top-bar ul li ul li a {
    border-width: 0;
}

#top-bar ul li ul a,
#top-bar a:hover {
    color: black;
}

.top-bar ul li:last-of-type ul {
    right: 0;
}

/* IE7 HACK */

#top-delul {
    *margin-top: -4px;
}

/* SIDE MENU */

#side-bar {
    border: 1px solid #dedede;
}

/* YUI-TABS */

.yui-navset .yui-content {
    background-color: #d4d8db;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: #d8d8d8;
    background-image: url(../images/yuitabs.png);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,

/* no focus effect for selected */

.yui-navset .yui-nav .selected a:hover {
    /* no hover effect for selected */
    background: #700 url(../images/yuitabs.png) repeat-x left -1400px;
    /* selected tab background */
    color: #fff;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #d88 url(../images/yuitabs.png) repeat-x left -1300px;
    text-decoration: none;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #444;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #444;
}

.yui-navset li {
    line-height: normal;
}

/* FOOTER */

#footer {
    clear: both;
    font-size: 77%;
    background: #444;
    color: #bbb;
}

#footer .options {
    visibility: visible;
    display: block;
    float: right;
    font-size: 100%;
    text-align: right;
}

#footer a {
    color: #fff;
    background: transparent;
}

h1,
#page-title {
    color: #EEE;
    font-weight: normal;
}

#page-title {
    border-color: #bbb;
}

ul {
    list-style: square;
}

li,
p {
    line-height: 141%;
}

a {
    color: #323232;
    text-decoration: none;
}

a.newpage {
    color: #323232;
    text-decoration: none;
}

a:hover {
    background: none;
    color: #545454;
}

/* SOME NICE BOXES */

div.sexy-box {
    background: #eee;
    border: 1px solid #ccc;
    overflow: hidden;
}

div.sexy-box div.image-container img {
    border: 1px solid #999;
}

blockquote {
    border: 1px solid white;
    background-color: #d4d8db;
}
[[/module]]

