[[module css]]

.hfcontainer {
    width: 100%;
    height: auto;
    overflow: visible;
    margin: 0;
    padding: 0;
    background-image: url('http://scpko.wdfiles.com/local--files/component%3Ahf-poster/Herman-Fuller-background.png');
    background-size: 100%;
    background-repeat: no-repeat;
    color: #BEB192;
    text-shadow: 2px 2px #000;
}

.hfheader {
    padding: 0;
    padding-top: 20px;
    height: 80px;
    width: 100%;
    display: block;
}

.hfnarrowcontent {
    width: 20%;
    font-size: 2em;
    font-family: eastmarketregular, sans-serif;
    text-align: center;
    padding: 0% 2%;
    padding-bottom: 0px;
    margin: 0;
    vertical-align: top;
    display: inline-block;
}

.hfnarrowcontent p {
    line-height: 110%;
}

.hftitle {
   font-size: 3.2em;
   font-family: eastmarketregular, sans-serif;
   font-weight: bold;
   text-align: center;
   height: 75px;
   width: 100%;
   padding: 0;
   margin: 0;
   display: block;
}

.hfwidecontent {
    width: 100%;
    height: auto;
    overflow: visible;
    font-size: 1.5em;
    font-family: eastmarketregular, sans-serif;
    text-align: center;
    padding-bottom: -20px;
    margin: 0;
    margin-top: 30px;
}

.hfwidecontent p {
    line-height: 120%;
    letter-spacing: 0.05em;
    padding: 0px 15px;
}

.hffooter {
    width: 100%;
    height: auto;
    overflow: visible;
    font-family: eastmarketregular, sans-serif;
    padding: 0px;
    margin-top: 20px;
    padding-bottom: 20px;
    text-align: center;
    font-size: 0.8em;
}

.hfpic {
    width: 50%;
    height: 100%;
    margin: 0;
    padding: 0;
    display: inline-block;
    background-position: center center; 
    vertical-align: middle;
}

.hfpicframe {
    background-image: url('http://scpko.wdfiles.com/local--files/component%3Ahf-poster/frame.png');
    background-repeat: no-repeat; 
    background-position: center center; 
    background-size: contain;
    width: 100%;
    height: 100%;
    margin: 0;
    padding: 0;
    display: inline-block;
}

.hfpic-container {
    width: 100%;
    height: 400px;
    margin: 0;
    padding: 0;
    display: block;
    text-align: center;
    vertical-align: middle;
}

.table {
    width: 100%;
    height: auto;
    overflow: visible;
    padding: 0;
    margin: 0;
}

@media only screen and (max-width: 1000px) {
    .hfnarrowcontent {
        font-size: 18pt;
    }

    .hfwidecontent p{
        font-size: 0.8em;
    }
}

@media only screen and (max-width: 975px) {
    .hftitle {
       font-size: 3.1em;
       height: 60px;
    }

    .hfpic {
        height: 95%;
    }
}

