[[module css]]
tt{
   font-family:var(--title-font);
   font-weight: 600;
   font-size:  100%
}



.a-brk{
display:block
}

.poem-xxx{
font-size:150%;
   margin: 0  auto;
   width: auto;
   left: auto;
   display: flex;
   flex-wrap: nowrap;
   word-break:  keep-all
}

.poem-xxx:before, .poem-xxx:after{
width:50%;
   content: ' ';   
   flex-shrink: 5;
}

.poem-xxx  li::marker {
   color: #999 !important;
        font-size:90%;
}

.poem-xxx ol > li::before {
    display: none !important;
}

.poem-xxx  ol > li{
    list-style-type: decimal-leading-zero;
       padding-left: 0em !important;
}

.poem-xxx  li::marker {
   color: #999 !important;
        font-size:90%;
}

.poem-xxx ol > li::before {
    display: none !important;
}

.poem-xxx  ol > li{
    list-style-type: decimal-leading-zero;
       padding-left: 0em !important;
}

@media (max-width: 479px) {    

.poem-xxx  li::marker {
   color: #999 !important;
}

.poem-xxx{
margin-left:0%
}
}


[[/module]]
