[[module css]]

.a-brk{
display:block
}

.poem-xxx{
font-size:150%;
}

.poem-xxx{
font-size:150%;
   margin: 0  auto;
   width: auto;
   left: auto;
   display: flex;
   flex-wrap: nowrap;
   white-space:nowrap;
}

.poem-xxx:before, .poem-xxx:after{
width:50%;
   content: ' ';   
   flex-shrink: 5;
}

.poem-xxx  li::marker {
   color: #999 !important;
        font-size:90%;
}

.poem-xxx ol > li::before {
    display: none !important;
}

.poem-xxx  ol > li{
    list-style-type: decimal-leading-zero;
          padding-left: 0 !important;
}

.poem-xxx * {
  font-family: 'Iceberg', sans-serif;
}


@media (max-width: 479px) {    
.poem-xxx  li::marker {
   color: #999 !important;
}

.poem-xxx{
   white-space:  inherit;   
}

.poem-xxx * {
  font-family: 'Iceland', sans-serif;
}
}

[[/module]]

