[[module CSS]]

@import url("https://use.typekit.net/seb3fgn.css");
@import url("https://use.typekit.net/seb3fgn.css");
@import url("https://use.typekit.net/seb3fgn.css");
@import url("https://use.typekit.net/seb3fgn.css");
@supports(display:grid) {


:root {

--test: #cf9f2d;

--header-title: "Sky Keeper Archives";
--header-subtitle: "Tales and legends of old before the empire of the Mad Monarch ";
--logo-image: url('http://scp-sandbox-3.wikidot.com/local--files/sky-keeper-archives-theme/Sky%20Keeper%20Archives%20Emblem.png');

/* Typefaces */
        --body-font: belda-normal, sans-serif;
        --header-font: cabazon, serif;
        --title-font: cabazon, serif;
        --UI-font: turnip, serif;
        --mono-font: pennsylvania, monospace;

/* Standard Colors */
        --white-monochrome: 252, 252, 252;
        /* white */
        --pale-gray-monochrome: 244, 244, 244;
        /* v light gray for blockquotes and stuff */
        --light-pale-gray-monochrome: 244, 244, 244;
        /* very light pale gray for misc. use */
        --very-light-gray-monochrome: 215, 215, 215;
        --light-gray-monochrome: 215, 215, 215;
        /* light accent gray for login status */
        --gray-monochrome: 176, 104, 60;
        /* gray */
        --dark-gray-monochrome: 105, 60, 51;
        /* dark accent gray for sidebar background */
        --black-monochrome: 26, 25, 24;
        /* black */
        --pale-accent: 252, 193, 135;
        --bright-accent: 196, 109, 81;
        /* bright red */
        --medium-accent: 232, 97, 70;
        /* medium red */
        --dark-accent: 87, 50, 36;
        /* dark red */
        --newpage-color: 181, 33, 0;
        /* pale orange */

--visited-link-color:var(--medium-accent);
}
#extrac-div-1 {
        background-image: url("http://scp-sandbox-3.wdfiles.com/local--files/sky-keeper-archives-theme/Sky-Keeper-Archives-Header.jpg");
       background-size: contain;
       position: absolute;
       top: 0;
       left: 0;
       background-repeat: repeat-x;
}
#extrac-div-2 {
display: none;
}
#header h2 span {
        margin-left: + 0.75rem;
margin-top: +7.5rem;
}
 #header h1 a::before {
        background: url(http://scp-sandbox-3.wikidot.com/local--files/the-enlightened-children/Sky%20Keeper%20Dragon%20Scales.png);
 -webkit-background-clip: text;
        background-clip: text;
        background-size: cover;
        color: rgb(var(--medium-accent));
        background-blend-mode: hard-light;
        -webkit-text-fill-color: transparent;
        overflow-wrap: normal;
}
#header h2 {
font-weight: 1000;
font-size: 200%;
font-style: normal;
 font-color: #ffe0cc;
}
div#extra-div-1 {
display: none;
}
 #page-title,
    .meta-title,
    #page-content h1,
    #page-content h2,
    #page-content h3,
    #page-content h4,
    #page-content h5,
    #page-content h6 {
        background: url(http://scp-sandbox-3.wikidot.com/local--files/the-enlightened-children/Sky%20Keeper%20Dragon%20Scales.png);
 -webkit-background-clip: text;
        background-clip: text;
        background-size: cover;
        color: rgb(var(--medium-accent));
        background-blend-mode: hard-light;
        -webkit-text-fill-color: transparent;
        overflow-wrap: normal;
}
table.wiki-content-table th {
        background: url(http://scp-sandbox-3.wikidot.com/local--files/the-enlightened-children/Sky%20Keeper%20Dragon%20Scales.png);
 -webkit-background-clip: text;
        background-clip: text;
        background-size: cover;
        color: rgb(var(--medium-accent));
        background-blend-mode: hard-light;
        -webkit-text-fill-color: transparent;
        overflow-wrap: normal;
        background-size: 100vw 100vh;
        background-attachment: fixed;
}
#interwiki .heading p,
    #side-bar .heading p {
        display: flex;
        position: relative;
        justify-content: center;
        margin: 1rem 0;
         background: url(http://scp-sandbox-3.wikidot.com/local--files/the-enlightened-children/Sky%20Keeper%20Dragon%20Scales.png);
        -webkit-background-clip: text;
        background-clip: text;
        background-size: cover;
        color: rgb(var(--swatch-menutxt-dark-color));
        color: rgb(var(--bright-accent));
        font-family: var(--header-font);
        font-size: 1.25rem;
        font-weight: 900;
        letter-spacing: 0.05rem;
        text-shadow: inherit;
        text-shadow: inherit;
        text-transform: none;
        -webkit-text-fill-color: transparent;
        overflow-wrap: normal;
    }
.fancyborder {
    box-sizing: border-box;
    padding: 2vw;
    border: 2vw solid rgba(0, 0, 0, 0.5);
    border-image: url("http://scp-sandbox-3.wikidot.com/local--files/the-enlightened-children/Dragon-Border.png") 600 round;
    border-image-width: 6;
}
[[/module]]

