[[module CSS]]
:root:lang(cn) {
    --logo-image: url("https://scp-wiki.wikidot.com/local--files/grigori-karpin-s-author-page/vanguard%20transparent.png");
    --header-title: "å…ˆé”‹";
    --header-subtitle: "åº‡æŠ¤ - å¸¸åŒ– - é€šæŠ¥";
    --header-h2-font-size: 1.1em;
}

#header::before {
    left: -12rem;
    filter: brightness(125%);
    opacity: 1;
}

#header h1 a::before {
    font-size: unset;
}

#header h2::before {
    color: #fe4;
}

#header h2 {
    top: 1em;
}

#header #login-status {
    left: -2vw;
}

@media (max-width: 767px) {
    #header::before {
        left: -7rem;
    }

    #header h1 {
        top: 0.5em;
        left: 0.5em;
    }
}

#page-title {
    display: none;
}

[[/module]]

[[module CSS]]
/* ä»https://scp-wiki.wikidot.com/scp-4991é‚£å„¿åšé¢œæ— è€»æŠ„æ¥çš„ä»£ç  */
/* 4CHAN */
.chan.thread-container {
  background-color: #FFFFEE;
  padding: 5px;
  font-family: arial,helvetica,sans-serif;
  color: #872737;
  overflow: hidden;
  font-size: 1.1em;
}
.chan.blue-container {
  background-color: #d6daf0;
  padding: 5px;
  font-family: arial,helvetica,sans-serif;
  color: #000;
  display: table;
  margin: 0 auto;
  overflow: hidden;
}
.chan.blue-container .post-content {
  margin-bottom: 0;
}

.chan .deleted-icon {
  color: #FF0000;
  background-color: #E3E3E3;
  border: 1px solid #383838;
  padding: 0 2px;
  font-weight: bold;
}

.chan .post-content {
  margin-block-start: 1em;
  margin-block-end: 1em;
  margin-inline-start: 40px;
  margin-inline-end: 40px;
}
.chan .summary {
  color: #707070;
  margin-top: 10px;
}
.chan .reply-container {
  display: table;
}
.chan .reply-arrows {
  color: #E0BFB7;
  float: left;
  margin-right: 2px;
  margin-top: 9px;
  margin-left: 2px;
}
.chan .reply {
  background-color: #F0E0D6;
  border: 1px solid #D9BFB7;
  border-left: none;
  border-top: none;
  display: table;
  padding: 5px;
  margin: 4px 0;
}

.chan img {
  float: left;
  margin-left: 20px;
  margin-right: 20px;
  margin-top: 3px;
  margin-bottom: 5px;
}
.chan .chan-file > p,
.chan .post-info > p,
.chan .reply-arrows > p,
.chan .summary > p,
.reply > p,
.chan.blue-container .post-content > p {
  margin: 0;
}
.chan .image-container {
  padding: 0;
}

@media (max-width: 479px) {
  .chan .reply-arrows {
    display: none;
  }
  .chan .post-content {
    margin: 0;
  }
  .chan .image-container {
    text-align: center;
  }
}

/* REDDIT */
.reddit-post{
    margin: .43em;
    padding: 0 .85em;

    background-color: #fcfcfb;
    border: 1px solid #b5b3ac;

    font-family: verdana,arial,sans-serif;
    overflow: hidden;
}
[[/module]]

[[module css]]
/* ä»https://scp-wiki.wikidot.com/toesplosion2020é‚£å„¿åšé¢œæ— è€»æŠ„æ¥çš„ä»£ç  */
.document {
display: inline-block;
border-radius: 10px;
border:solid 1px #ccc;
background:#f0f0f0;
float:top; width:96.5%;
padding: 10px;
box-shadow: 0 2px 4px rgba(68, 68, 68, .5);
}

.click{
cursor: pointer;
}

.ping,
.fakedl{
color: #39e;
cursor: pointer;
font-weight: bold;
}

.edit{
color: #666;
font-size: 75%;
}

.edit:hover{
cursor: default;
title: "test";
}

.ping:hover{
text-decoration: underline;
}

.member{
color: #f33;
}

.member0{
color: #fff;
}

.member2{
color: #1f1;
}

.member3{
color: #838;
}

.member4{
color: #fc2;
}

.member5{
color: #0c9;
}

.member6{
color: #c04;
}

.member7{
color: #d60;
}

.member:hover,
.member0:hover,
.member2:hover,
.member3:hover,
.member4:hover,
.member5:hover,
.member6:hover,
.member7:hover,
.fakedl:active{
text-decoration: underline;
cursor: pointer;
}

blockquote {
color: #eee;
background-color: #223;
border: solid 1px #444;
}

span.member:active,
span.member0:active,
span.member2:active,
span.member3:active,
span.member4:active,
span.member5:active
span.member6:active
span.member7:active
span.ping:active,
span.click:active{
background-color: #99f;
border-radius: 4px;
}

span.link a{
color: #39e;
}

.newmes{
color: #d00;
}

.red-hr > hr{
background-color: #d00;
bottom: 0.2em;
}
[[/module]]

