[[module CSS]]
:root {
    --logo-image: url("https://scp-wiki.wikidot.com/local--files/grigori-karpin-s-author-page/vanguard%20transparent.png");
    --header-title: "ANGUARD";
    --header-subtitle: "SHELTER - NORMALIZE - INFORM";
    --header-h2-font-size: 1.1em;
}

#header::before {
    left: -12rem;
    filter: brightness(125%);
    opacity: 1;
}

#header h1 a::before {
    font-size: unset;
}

#header h2::before {
    color: #fe4;
}

#header h2 {
    top: 1em;
}

#header #login-status {
    left: -2vw;
}

@media (max-width: 767px) {
    #header::before {
        left: -7rem;
    }

    #header h1 {
        top: 0.5em;
        left: 0.5em;
    }
}

#page-title {
    display: none;
}

[[/module]]

[[module CSS]]
/* code shamelessly stolen from https://scp-wiki.wikidot.com/scp-4991 */
/* 4CHAN */
.chan.thread-container {
  background-color: #FFFFEE;
  padding: 5px;
  font-family: arial,helvetica,sans-serif;
  color: #872737;
  overflow: hidden;
  font-size: 1.1em;
}
.chan.blue-container {
  background-color: #d6daf0;
  padding: 5px;
  font-family: arial,helvetica,sans-serif;
  color: #000;
  display: table;
  margin: 0 auto;
  overflow: hidden;
}
.chan.blue-container .post-content {
  margin-bottom: 0;
}

.chan .deleted-icon {
  color: #FF0000;
  background-color: #E3E3E3;
  border: 1px solid #383838;
  padding: 0 2px;
  font-weight: bold;
}

.chan .post-content {
  margin-block-start: 1em;
  margin-block-end: 1em;
  margin-inline-start: 40px;
  margin-inline-end: 40px;
}
.chan .summary {
  color: #707070;
  margin-top: 10px;
}
.chan .reply-container {
  display: table;
}
.chan .reply-arrows {
  color: #E0BFB7;
  float: left;
  margin-right: 2px;
  margin-top: 9px;
  margin-left: 2px;
}
.chan .reply {
  background-color: #F0E0D6;
  border: 1px solid #D9BFB7;
  border-left: none;
  border-top: none;
  display: table;
  padding: 5px;
  margin: 4px 0;
}

.chan img {
  float: left;
  margin-left: 20px;
  margin-right: 20px;
  margin-top: 3px;
  margin-bottom: 5px;
}
.chan .chan-file > p,
.chan .post-info > p,
.chan .reply-arrows > p,
.chan .summary > p,
.reply > p,
.chan.blue-container .post-content > p {
  margin: 0;
}
.chan .image-container {
  padding: 0;
}

@media (max-width: 479px) {
  .chan .reply-arrows {
    display: none;
  }
  .chan .post-content {
    margin: 0;
  }
  .chan .image-container {
    text-align: center;
  }
}

/* REDDIT */
.reddit-post{
    margin: .43em;
    padding: 0 .85em;

    background-color: #fcfcfb;
    border: 1px solid #b5b3ac;

    font-family: verdana,arial,sans-serif;
    overflow: hidden;
}
[[/module]]

[[module css]]
/* code shamelessly stolen from https://scp-wiki.wikidot.com/toesplosion2020 */
.document {
display: inline-block;
border-radius: 10px;
