@media screen and (-webkit-min-device-pixel-ratio:0) {
body:before {
  content: ' ';
  position: fixed;
  z-index: -1;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background: url(http://scp-wiki-cn.wikidot.com/local--files/the-first-starourenk-retreat/SCP_Foundation_%28emblem%29.svg) center 0 no-repeat;
  background-size: 30% auto;
background-position:center;
}
[[/module]]

[[module CSS]]
#header {
  background: none;
}
#header::before {
  display: block;
  position: absolute;
  width: 90%;
  height: 140px;
  top: 0;
  left: 0;
  z-index: -1;
  content: "";
  background: url(http://scpsandboxcn.wikidot.com/local--files/echoaphaniptera2/sral%20logo.png) 5px 20px no-repeat;
  background-size: 88px 88px;
}
@media (max-width: 479px) {
  #header::before {
    background-position: 0 5.5em;
    background-size: 55px 55px;
  }
}
@media (min-width: 480px) and (max-width: 580px) {
  #header::before {
    background-position: 0.5em 4.5em;
    background-size: 66px 66px;
  }
}
@media (min-width: 581px) and (max-width: 767px) {
  #header::before {
    background-position: 1em 4em;
    background-size: 77px 77px;
  }
}
@media (min-width: 768px) and (max-width: 979px) {
#header::before {
    background-position: 1em 4em;
    background-size: 88px 88px;
  }
}
#header h1 {
  -webkit-animation: flicker-1 10s infinite;
  -o-animation: flicker-1 10s infinite;
  animation: flicker-1 10s infinite;
}
#header::before {
  -webkit-animation: flicker-2 10s infinite;
  -o-animation: flicker-2 10s infinite;
  animation: flicker-2 10s infinite;
}
#header h2 {
  -webkit-animation: flicker-3 10s infinite;
  -o-animation: flicker-3 10s infinite;
  animation: flicker-3 10s infinite;
}
@keyframes flicker-1 {
  53% {
    -webkit-filter: brightness(100%);
    filter: brightness(100%);
  }
