[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Iceberg&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Philosopher&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Domine&display=swap');

/* the background and the header color, as well as select elements (the byline and the fancyborder) were taken from Flopstyle DARK. The fonts were selected based on FRATRICIDE and SCP-8166*/

:root{
    --header-gold: 211, 166, 71;
    --header-light: 255, 234, 190;
    --dark-yellow: 126, 103, 53;

    --body-font-primary: 'Philosopher'; 

    /* ----------------- colors ----------------- */
    --basalt-primary-color: 47, 51, 60;
    --basalt-secondary-color: 36, 39, 46;
    --basalt-tertiary-color: 211, 166, 71;

    --basalt-undertone: 162, 68, 127;
    --basalt-bright-element-color: 198, 93, 160; 
    --basalt-dark-element-color: 198, 93, 160;
    --basalt-UI-dark-palette: 90, 38, 71;
    --basalt-light-text-color: 182, 165, 129;

    --basalt-alternate-color: 126, 82, 32;
    --basalt-positive-color: 236, 160, 208;
    --basalt-negative-color: 176, 164, 228;
    --general-border-color: 128, 92, 21;

    /* ----------------- header ----------------- */
    --header-logo: url('https://scp-wiki.wdfiles.com/local--files/her-golden-wings/image.png');
    --header-title: 'Amoni-Ram';
    --header-font-primary: 'Iceberg';
    --header-title-color: var(--header-gold);
    --header-background-color: 21, 24, 30;
    --base-header-height: calc((var(--title-size) + var(--subtitle-size)) *1.325 + 1rem);
    --subtitle-size: 1rem;

    /* ----------------- top bar ----------------- */
    --top-bar-link-color: var(--basalt-light-text-color);
    --top-bar-dropdown-background: var(--basalt-UI-dark-palette);
    --top-bar-dropdown-background-hover: var(--basalt-undertone);

    /* ----------------- sidebar ----------------- */
    --side-bar-background-color: 36, 39, 46;
    --side-bar-heading-text-color: var(--header-gold);
    --side-bar-heading-text-color-hover: var(--header-gold);
    --side-bar-heading-background-color: var(--header-background-color);
    --side-bar-link-color: var(--top-bar-link-color);
    --side-bar-link-background-hover: var(--top-bar-dropdown-background);

    /* ----------------- edit box, footer, rate module ----------------- */
    --rate-module-background-color: var(--dark-yellow);
    --bottom-area-text-color: var(--top-bar-link-color);
    --footer-link-color: var(--header-gold);
    --license-area-divider-color: var(--basalt-tertiary-color);
    --edit-area-toolbar-background-color: var(--dark-yellow);
    --edit-area-ancillary-background-color: var(--edit-area-toolbar-background-color);

    /* ----------------- misc ----------------- */
    --hr-color: 128, 92, 21;
    --title-border-color: 128, 92, 21;
    --button-text-color: var(--top-bar-link-color);
    --blockquote-background-color: var(--header-background-color);
    --scrollbar-track-color: var(--dark-yellow);
    --scrollbar-thumb-color: var(--basalt-light-text-color);
}

#header h1 a span::after {
    display: none;
}

#page-title, .meta-title {
    margin-top: 1em;
    border-bottom: solid 0.125rem rgb(128, 92, 21);
    color: rgb(var(--header-gold));
    font-size: 3em;
 } 

:is(#side-bar, #interwiki) .heading {
    font-size: calc(var(--header-UI-font-size) * .5);
}

p {
    font-size: 1.15rem;
    line-height: 1.3;
    text-indent: 2rem;
    text-align: justify;
}

blockquote, div.blockquote {
    background-color: rgba(var(--blockquote-background-color), 0.5);
}

.collapsible-block-content p {
	font-size: var(--true-font-size);
	text-indent: unset;
}

#html-body .hovertip {
    font-family: var(--UI-font);
    color: rgb(var(--basalt-light-text-color))!important;
}

div[id*="page-options-bottom"] > a {
    color: rgb(var(--basalt-light-text-color))!important;
}

#more-options-button, #delete-button {
    background-color: rgb(var(--dark-yellow))!important;
    color: rgb(var(--basalt-main-text-color))!important;
}

#delete-button {
    color: rgb(var(--basalt-light-text-color))!important;
}

div[id*="page-options-bottom"] > a:is(:hover,:focus) {
    color: rgb(var(--basalt-light-text-color))!important;
}

#license-area::after {
    background-image: unset;
}

/* ---- BYLINE from Flopstyle DARK ---- */
 .byline {
    display: flex;
    margin-top: -.13rem;

    text-align: center;
}

 .byline p {
    display: flex;
    flex-direction: column;
    align-items: center;
    max-width: 55%;
    margin: auto; 
    font-size: 95%;
    text-indent: unset;

    line-height: 0.5;
}

 @media (max-width: 767px) {
      .byline p {
        max-width: unset;
    }
}

 .byline span:nth-of-type(1):before,
 .byline span:nth-of-type(2):before {
    padding-right: .27rem;

    color: var(--header-light);

    font: 100%/1 "Lucida Sans Unicode", "Lucida Grande", "Arial Unicode MS", sans-serif;
}

 .byline span:nth-of-type(1):before {
    color: rgb(128, 92, 21);
    content: "✎"
}

 .byline span:nth-of-type(2):before {
    content: " "
}

 .byline span:nth-of-type(2):after {
    content: " "
}

/* ---- FANCYBORDER from Flopstyle DARK ---- */
 .fancyborder {
     margin: auto 3vw;
     padding: 2vw;
     font-family: 'Domine', var(--body-font-fallback);

     border: 2vw solid;
     border-image: url(https://scp-wiki.wikidot.com/local--files/theme:flopstyle-dark/wl_border.png) 600 round;
     border-image-width: 6;
}

.fancyborder p {
     font-size: 1rem;
     font-style: italic;
}

