[[module CSS]]
@import url(https://scp-wiki.wikidot.com/theme:ad-abyssum/code/1);

div#container-wrap {
    background-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Aad-abyssum/tacttheo_banner_4.png");
}

#header {
    background-image: url("https://upload.wikimedia.org/wikipedia/commons/7/7a/WeirdTalesv36n02pg083_sunrise_ornament.png");
    background-position: center;
    background-size: 40%;
    height: 10rem;
}
#header h1,
#header h2 {
    float: initial;
    text-align: center;
    margin-left: 0rem;
}

#header h1 a {
    line-height: 8.75rem;
}

#header h2 {
    line-height: 11rem;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 a::before {
    color: #ff8000;
    content: "The Sun";
    font-family: 'Martel', serif;
    text-shadow: 0rem 0rem 0.5rem #fff;
}

#header h2::before {
    color: #3790c0;
    content: "and the Coming of The Dawn";
    font-family: 'Martel', serif;
    text-shadow: 0rem 0rem 0.1rem #fff;
}

[[/module]]

[[module css]]
:root{
/* ---------- Box variables ---------- */
    --blockquote-background-color: 47, 51, 60;
    --UI-opacity: 0.6875;
    --blockquote-border-width: .0875rem;
    --blockquote-border-color: 240, 164, 0;
    --window-shadow: 0 0 0.625rem rgb(var(--UI-dark-palette),0.5);

    --UI-dark-palette: 56, 60, 64;
/* -------------------- */
}

/* ---------- Box code ---------- */
.box {
    display: grid;
    grid-template-areas:
        "pic title"
        "pic desc"
        "pic desc";
    grid-template-columns: 30% 70%;

    background-color: rgba(var(--blockquote-background-color),var(--UI-opacity));
    border-radius: 1.5rem;
    border: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
    box-shadow: var(--window-shadow);
    margin-bottom: 1em;
}

.pic {
    grid-area: pic;
    display: grid;
    align-self: center;

    padding: 1em;
    transition: transform .30s;
}

.pic:hover {
    transform: scale(1.25);
    z-index: 2;
}
.pic:focus-within {
    transform: scale(1.25);
    z-index: 2;
}

.title {
    display: grid;
    grid-area: title;
    align-self: center;

    text-align: center;
    color: #ff8000;
    padding: 0 1em;
}

.title h2 {
    margin-bottom: 0;
}

.desc {
    grid-area: desc;
    display: grid;
    align-self: center;

    padding: 1em;
    text-align: center;
    color: #ffffff;
}

.desc p {
    margin-top: 0;
    margin-bottom: 0.5rem;
}

@media only screen and (max-width: 767.98px) {

.box {
    display: grid;
    grid-template-areas:
        "title title"
        "pic pic"
        "desc desc";

    border: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
}

.desc {
    border-top: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
}

div.skip_window {
    padding: 0 0.5em;
    margin: 1.5rem 0;
}

.skip_window::before {
    padding: 0.625em 1.5rem;
    margin: 1.5rem -0.6em;
}

.yui-navset.yui-navset-top .yui-content {
    padding: 1em 0.5em;
}

}

/* -------------------- */
[[/module]]

