function sendMessage(event) {
  event.preventDefault();
  const messageElem = document.getElementById('message');
  const encKeyElem = document.getElementById('encKey');
  const contactResult = document.getElementById('contactResult');
  const message = messageElem.value;
  const key = encKeyElem.value.trim();
  if (message.trim() === '' || key === '') {
    contactResult.innerHTML = `<p style="color: #9c173b;">Your message or encryption key is empty. The cosmic code demands substance.</p>`;
    triggerHack();
    return;
  }
  if (key !== correctEncryptionKey) {
    contactResult.innerHTML = `<p style="color: #9c173b;">Encryption key incorrect! The cosmic order is disturbed.</p>`;
    lives--;
    updateLivesDisplay();
    playSound("lifeLost");  
    notifyOrCmd("-1 PSYCHIC RESISTANCE");
    return;
  }

  const lowerMessage = message.toLowerCase();
  let responseFound = false;

  for (const keyword in queryResponses) {
    if (lowerMessage.includes(keyword)) {

      contactResult.innerHTML = `<p style="color: #0ce6f2;">${queryResponses[keyword]}</p>`;
      inboxMessages.push(`Reply: ${queryResponses[keyword]}`);
      responseFound = true;
      break;
    }
  }

  if (!responseFound) {
    contactResult.innerHTML = `<p style="color: #0ce6f2;">Message transmitted through the ether. Await our spectral response...</p>`;
    inboxMessages.push("Your recent query has been received by the Dark One.");
  }

  messageElem.value = '';
  encKeyElem.value = '';

  if (!currencyEarned.contact) {
    earnCurrency(3);
    currencyEarned.contact = true;
  }

  notifyOrCmd("Your message drifts into the void...");
}

