  } else if (simState === "right2a") {
    endingPage = "endingEclipse";
    if (bonus[2] === 0) {earnCurrency(5); bonus[2] = 1;}
  } else if (simState === "right2b") {
    endingPage = "endingOblivion";
    if (bonus[3] === 0) {earnCurrency(5); bonus[3] = 1;}
  }
  if (bonus.every(val => val === 1) && !visitedDoom) {
    endingPage = "endingDoom";
    visitedDoom = true;
  }
  showSectionWithDelay(endingPage);

  if (endingPage === "endingDoom") {
    setTimeout(() => {
      if (bonus.every(val => val === 1)) {
        let doomDiv = document.getElementById("endingDoom");
        if (doomDiv) {
          let button = document.createElement("button");
          button.textContent = "JOIN US";
          button.className = "game-button click";
          button.onclick = () => showSectionWithDelay("gathering");
          doomDiv.appendChild(button);
        }
      }
    }, 1000);
  }
}
