function processCommand(input) {
  if (!input) { const cmdInput = document.getElementById("commandInput"); input = cmdInput ? cmdInput.value : ""; }
  if (!input.trim()) return;
  let cmd = input.trim().toLowerCase();
  let output = document.getElementById("cmdOutput");
  if (!output) return;
  cmd = cmd.replace(/</g, "&lt;").replace(/>/g, "&gt;").replace(/&/g, "&amp;");
  if (pendingReset) {
    if (cmd === "y") {
      output.innerHTML += "Reset confirmed. Restarting system...\n";
      pendingReset = false;
      notifyOrCmd("System restarting...");
      setTimeout(() => {
        resetGameState();
        location.reload();
      }, 1500);
      return;
    } else if (cmd === "n") {
      output.innerHTML += "Reset cancelled.\n";
      pendingReset = false;
      return;
    } else {
      output.innerHTML += "Please confirm with 'y' or 'n'.\n";
      return;
    }
  }
  output.innerHTML += "> " + cmd + "\n";
  if (cmd === "/help") {
    output.innerHTML += "Available commands:\n";
    output.innerHTML += "/help - Display available commands.\n";
    output.innerHTML += "/inject [ip] - Extract data from a detected IP.\n";
    output.innerHTML += "/scan - Scan for hidden IP addresses.\n";
    output.innerHTML += "/reset - Reset system (clear save and restart).\n";
    output.innerHTML += "/log - View notification history.\n\n";
  } else if (cmd === "/scan") {
    notifyOrCmd("Scanning for hidden IP...");
    runScanProcess();
  } else if (cmd.startsWith("/inject ")) {
    let parts = cmd.split(" ");
    let ip = parts[1];
    if (!ip || ip.trim() === "") {
      notifyOrCmd("Error: No IP provided. Use '/inject [IP]' to specify an IP address.");
    } else {
      notifyOrCmd("Injecting data from IP " + ip + "...");
      runInjectionProcess(ip);
    }
  } else if (cmd === "/reset") {
    output.innerHTML += "Are you sure you want to reset? (y/n)\n";
    pendingReset = true;
    return;
  } else if (cmd === "/log") {
    output.innerHTML += "Notification history:\n";
    if (Array.isArray(toastHistory) && toastHistory.length > 0) {
      toastHistory.forEach((msg) => {
        output.innerHTML += "- " + msg + "\n";
      });
    } else {
      output.innerHTML += "No notification history available.\n";
    }
    output.innerHTML += "\n";
    notifyOrCmd("Log history displayed");
  } else {
    output.innerHTML += "Unknown command. Type /help for available commands.\n";
  }
  output.scrollTop = output.scrollHeight;
}
