function generateFileHash() {
  const chars = "abcdef0123456789";
  let hash = "";
  for (let i = 0; i < 8; i++) {
    hash += chars[Math.floor(Math.random() * chars.length)];
  }
  return hash;
}
function generateRandomDomain() {
