[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Prata&display=swap');

:root { --body-font: 'Noto Sans Cuneiform', 'Inter', 'Arial', sans-serif; 
  --header-height: clamp(10rem,55vmin,40rem);
  --header-image: url("https://i.imgur.com/1iTLgvG.jpg");
  --footer-image: url("https://i.imgur.com/1iTLgvG.jpg");
}

#footer, #extrac-div-1, #extrac-div-1 span {
    background-image: var(--footer-image);
    background-size: 100vmax;
    background-attachment: fixed;
    background-position: bottom;
    height: clamp(10rem,55vmin,30rem);
    background-repeat: no-repeat;
}

#extra-div-1 {
    background-position: top;
}

.bubble {
    margin: 10px;
    padding: 24px;
    border: 3px solid black;
    border-radius: 12px;
    background-color white;
    min-height: 150px;
}

}
.scp-image-block .scp-image-caption{
    font-size: 1.5em;
}

#wiki-tab-0-0 .bubble .scp-image-block .scp-image-caption{
    display: none;
}

/*---- Wikimodule integration ----*/

body {
    --wm-bg: var(--foamy-white);
    --wm-accent: var(--lagoon-hue);
    --wm-border: var(--undersea-tone);
}

/*---- Custom divs ----*/


}

.warning-tide {
    box-sizing: border-box;
    border: solid .175rem rgb(var(--stormy-sky));
    position: relative;
    padding: 1rem;
    margin-inline: auto;

    font-family: var(--mono-font);
    font-size: 1.125em;
    color: rgb(var(--stormy-sky));
    background-color: rgb(var(--undersea-tone));
}
.warning-tide::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background: linear-gradient(rgb(18, 16, 16, 0.01) 50%, rgb(0, 0, 0, 0.25) 50%), linear-gradient(90deg, rgb(255, 0, 0, 0.1), rgb(0, 255, 0, 0.06), rgb(0, 0, 255, 0.1));
    background-size: 100% 2px, 3px 100%;
    opacity: .875;
    z-index: 1;
    pointer-events: none;
}

.opener {
    font-size: 1.125em;
    padding: 1.325rem;
    padding-block-end: 0.5rem;
    margin-block-start: 6.25rem;
}

.warning-header {
    font-weight: bold;
    text-align: center;
    font-size: 1.5em;
    padding: 0.25rem;
    color: rgb(var(--undersea-tone));
    background-color: rgb(var(--stormy-sky));
}


.warning-tide .hili {
    font-weight: bold;
    padding-inline: .125em;
    color: rgb(var(--undersea-tone));
    background-color: rgb(var(--stormy-sky));
}

/*-------------------------------*/

.mither {
    font-family: var(--header-font);
    color: green;
    font-size: 1.375em;
    line-height: 1.275;
}
.mither p { line-height: inherit; }

.scenesetter {
    padding-inline: 1.75rem;
    position: relative;
    padding-block: 3.5rem;
}
.scenesetter::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background-image: var(--header-image);
    background-size: cover;
    background-position: center;
    opacity: 0.225;
    box-shadow: inset 0 0 4rem 4.25rem rgb(var(--foamy-white));
    filter: blur(1.375rem);
    z-index: -1;
}

.glasscase{
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}
.glassyeye{
    aspect-ratio : 1 / 1;
    width: 50%;
    margin-top: -24px;
    position: relative;
    transition: 0.5s !important;
}
.glassyeye:hover{
    filter: hue-rotate(180deg);
}
.glassyeye img{
    height: 100%;
    width: 100%;
    object-fit: cover;
    object-position: center;
}
.allfolks{
    position: absolute;
    z-index: 5;
    top: 0px;
    left: 10px;
    font-size: 4vh;
    mix-blend-mode: difference;
    filter: invert(1) grayscale(1) contrast(9);

.allfolks a{
    font-weight: bold;
    color:red !important; /*ADD SPECIFICALLY THIS TO CSS*/
}

@media screen and (min-width: 1200px) {
.allfolks{
    font-size: 25px;
}
}
@media screen and (max-width: 640px) {
.glasscase{
    flex-direction: column;
}
.allfolks{
    font-size: 4vw;
    top: 3px;
}
.glassyeye{
    margin-top: 0px;
    width: 100%;
}

