[[module CSS]]
.imagediv { float: right; margin: 15px}
@media (max-width: 540px) {
  .imagediv {
    float: none; text-align:center; margin: auto;
  }
}

div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "Holy Foundation";
    color: #eee;

}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "Servitude, Chivalry, Piety";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

#header {
  background-image: url(https://scp-wiki.wdfiles.com/local--files/art%3Adoctors-of-the-church-hub/Holy-Foundation.svg);
  background-size: 90px;
}
