[!--
段落页：
* http://scp-wiki-cn.wikidot.com/fragment:the-moons-stellar-companion-0
* http://scp-wiki-cn.wikidot.com/fragment:the-moons-stellar-companion-1
--]
[[module css]]
@import url('https://fonts.googleapis.com/css?family=ZCOOL+QingKe+HuangYou');

#page-content {
	font-size: 120%;
}


#page-content > p {
	padding: 0 2em;
}

.poem {
	position: relative;
	display: -webkit-box;
	display: -webkit-flex;
	display: -moz-box;
	display: -ms-flexbox;
	display: flex;
	-webkit-box-align: center;
	-webkit-align-items: center;
	   -moz-box-align: center;
	    -ms-flex-align: center;
	        align-items: center;
	-webkit-box-pack: center;
	-webkit-justify-content: center;
	   -moz-box-pack: center;
	    -ms-flex-pack: center;
	        justify-content: center;
	width: 100%;
	background: url(http://scp-wiki.wdfiles.com/local--files/the-moons-stellar-companion/galfed_circle.png);
	-webkit-background-size: contain;
	        background-size: contain;
	-webkit-flex-shrink: 2;
	    -ms-flex-negative: 2;
	        flex-shrink: 2;
}

.poem::before {
	content: " ";
	position: absolute;
	width: 100%;
	height: 100%;
	background: url(http://scp-wiki.wdfiles.com/local--files/the-moons-stellar-companion/galfed_circle.png);
	-webkit-background-size: contain;
	        background-size: contain;
	-webkit-animation: rotation 300s infinite linear;
	   -moz-animation: rotation 300s infinite linear;
	     -o-animation: rotation 300s infinite linear;
	        animation: rotation 300s infinite linear;
	-webkit-animation-play-state: paused;
	   -moz-animation-play-state: paused;
	     -o-animation-play-state: paused;
	        animation-play-state: paused;
}

.poem:hover::before {
	-webkit-animation-play-state: running;
	   -moz-animation-play-state: running;
	     -o-animation-play-state: running;
	        animation-play-state: running;
}

.poem::after {
	content: " ";
	display: block;
	padding-bottom: 100%;
}

.poem > .stanza {
	--wght: 600;
	width: 25%;
	position: relative;
	text-align: center;
	font-family: 'ZCOOL QingKe HuangYou', var(--title-font);
	font-size: -webkit-calc(var(--base-font-size) * 1.2);
	font-size: -moz-calc(var(--base-font-size) * 1.2);
	font-size: calc(var(--base-font-size) * 1.2);
	font-weight: 600;
	padding: -webkit-calc(var(--body-width-on-desktop) * 0.0125);
	padding: -moz-calc(var(--body-width-on-desktop) * 0.0125);
	padding: calc(var(--body-width-on-desktop) * 0.0125);
}

.stanza > div {
	position: absolute;
	width: 100%;
	top: 0;
	left: 0;
}

.stanza > .chinese,
.stanza > .english {
	-webkit-transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
	-o-transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
	-moz-transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 400ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 400ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.stanza > .chinese {
	--wght: 500;
	opacity: 1;
	-webkit-filter: blur(0);
	        filter: blur(0);
	font-weight: 500;
}

.stanza > .english {
	opacity: 0;
	-webkit-filter: blur(3em);
	        filter: blur(3em);
}

.poem:hover .chinese {
	opacity: 0;
	-webkit-filter: blur(3em);
	        filter: blur(3em);
}

.poem:hover .english {
	opacity: 1;
	-webkit-filter: blur(0);
	        filter: blur(0);
}

.poem:hover .chinese,
.poem:hover .english {
	-webkit-transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
	-o-transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
	-moz-transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
	transition: opacity 200ms cubic-bezier(0.4, 0.0, 0.2, 1), filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1), -webkit-filter 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.poem > .stanza > .chi,
.poem-font .chi {
	font-family: var(--title-font);
}

.chi {
	height: 100%;
	width: 0.5em;
	display: inline-block;
	position: relative;
}

.chi::before {
	position: absolute;
	left: 0;
	width: 100%;
	height: 100%;
}

.english > span:nth-child(3)::before {
	content: "ˇ";
	top: 0;
}

.english > span:nth-child(4)::before {
	content: "`";
	top: 0;
}

#page-content > p:nth-child(5) .chi::before {
	content: "`";
	top: 0;
	left: 0.15em;
}


.poem-font .chi::before {
	content: "-";
	top: -0.5em;
}

.stanza.one {
	margin-top: -webkit-calc(var(--body-width-on-desktop) * -0.4);
	margin-top: -moz-calc(var(--body-width-on-desktop) * -0.4);
	margin-top: calc(var(--body-width-on-desktop) * -0.4);
}

.stanza.two {
	margin-top: -webkit-calc(var(--body-width-on-desktop) * -0.1375);
	margin-top: -moz-calc(var(--body-width-on-desktop) * -0.1375);
	margin-top: calc(var(--body-width-on-desktop) * -0.1375);
}

.stanza.three {
	margin-top: -webkit-calc(var(--body-width-on-desktop) * 0.1375);
	margin-top: -moz-calc(var(--body-width-on-desktop) * 0.1375);
	margin-top: calc(var(--body-width-on-desktop) * 0.1375);
}

.stanza.four {
	margin-top: -webkit-calc(var(--body-width-on-desktop) * 0.4);
	margin-top: -moz-calc(var(--body-width-on-desktop) * 0.4);
	margin-top: calc(var(--body-width-on-desktop) * 0.4);
}

.poem-font {
	--wght: 600;
	font-size: -webkit-calc(var(--base-font-size) * 1.2);
	font-size: -moz-calc(var(--base-font-size) * 1.2);
	font-size: calc(var(--base-font-size) * 1.2);
	font-family: var(--title-font);
	font-weight: 600;
}

.glitch {
	position: relative;
	opacity: 0.9;
}

.glitch::before,
.glitch::after {
	content: attr(data-text);
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
}

.glitch::before {
	left: 0.125rem;
	text-shadow: -0.0625rem 0 rgba(var(--bright-accent));
	animation: glitch-anim-1 14s infinite linear alternate-reverse;
	-webkit-transform: scaleX(-1);
	   -moz-transform: scaleX(-1);
	    -ms-transform: scaleX(-1);
	     -o-transform: scaleX(-1);
	        transform: scaleX(-1);
	-webkit-filter: FlipH;
	        filter: FlipH;
	opacity: 0.75;
}

.glitch::after {
	left: -0.125rem;
	text-shadow: -0.0625rem 0 rgba(var(--dark-accent));
	animation: glitch-anim-2 10s infinite linear alternate-reverse;
	opacity: 0.4;
}

.leftTriangle,
.rightTriangle {
	width: 40%;
	height: var(--body-width-on-desktop);
	content: " ";
	clear: unset !important;
}

.squish {
	height: var(--body-width-on-desktop);
	position: relative;
}

#page-content > div.squish > br {
	display: none;
}

.sidd {
	padding: 25em 2em 2em;
	margin: 0 0 1em 0;
	position: relative;
}

.sidd > p:nth-child(1) {
	margin-top: 0;
	padding-top: 1em;
}

.sidd::before,
.sidd::after {
	content: " ";
	position: absolute;
	left: 0;
	top: 0;
	width: 100%;
	height: 100%;
}

.sidd::after {
	background: rgba(var(--dark-accent));
	z-index: -2;
	-webkit-border-radius: 0 0 2em 2em;
	        border-radius: 0 0 2em 2em;
}

/* Supports */

@supports (mix-blend-mode: screen) {
	.poem::before {
		mix-blend-mode: screen;
	}

	.squish::after {
		mix-blend-mode: soft-light;
	}

	.sidd:before {
		mix-blend-mode: exclusion;
	}
}

@supports ((-webkit-clip-path: url("")) or (clip-path: url(""))) {
	.glitch::before {
		background: rgba(var(--black-monochrome));
		-webkit-clip-path: polygon(0 24%, 100% 24%, 100% 64%, 0 64%);
		clip-path: polygon(0 24%, 100% 24%, 100% 64%, 0 64%);
	}

	.glitch::after {
		background: rgba(var(--black-monochrome));
		-webkit-clip-path: polygon(0 74%, 100% 74%, 100% 91%, 0 91%);
		clip-path: polygon(0 74%, 100% 74%, 100% 91%, 0 91%);
	}

}

@supports (((-webkit-mask-image: url("")) or (mask-image: url(""))) or (-webkit-mask-image: url(""))) {
	.sidd:before {
		background-image: url(http://scp-wiki.wdfiles.com/local--files/theme%3Agalactic-theme/galfed_bg.png);
		z-index: -1;
		-webkit-background-size: contain;
		        background-size: contain;
		background-repeat: no-repeat;
		background-position: top;
		-webkit-transform: rotate(180deg);
		   -moz-transform: rotate(180deg);
		    -ms-transform: rotate(180deg);
		     -o-transform: rotate(180deg);
		        transform: rotate(180deg);
		mask-image:
			-webkit-gradient(linear,
				left top, left bottom,
					from(rgba(0, 0, 0, 1)),
					color-stop(10%, rgba(0, 0, 0, 0)));
		mask-image:
			linear-gradient(to bottom,
				rgba(0, 0, 0, 1) 0%,
				rgba(0, 0, 0, 0) 10%);
		-webkit-mask-image:
			-webkit-gradient(linear,
				left top, left bottom,
					from(rgba(0, 0, 0, 1)),
					color-stop(10%, rgba(0, 0, 0, 0)));
		-webkit-mask-image:
			-webkit-linear-gradient(top,
				rgba(0, 0, 0, 1) 0%,
				rgba(0, 0, 0, 0) 10%);
		-webkit-border-radius: 2em 2em 0 0;
		        border-radius: 2em 2em 0 0;
	}

	.sidd::after {
		background: rgba(var(--dark-accent));
		z-index: -2;
		-webkit-border-radius: 0 0 2em 2em;
		        border-radius: 0 0 2em 2em;
		mask-image: -webkit-gradient(linear, left bottom, left top, color-stop(50%, rgba(0, 0, 0, 1)), to(rgba(0, 0, 0, 0)));
		mask-image: linear-gradient(to top, rgba(0, 0, 0, 1) 50%, rgba(0, 0, 0, 0) 100%);
		-webkit-mask-image: -webkit-gradient(linear, left bottom, left top, color-stop(50%, rgba(0, 0, 0, 1)), to(rgba(0, 0, 0, 0)));
		-webkit-mask-image: -webkit-linear-gradient(bottom, rgba(0, 0, 0, 1) 50%, rgba(0, 0, 0, 0) 100%);
		padding-top: 50rem;
		margin-top: -50rem;
	}
}

@media only screen and (max-width: 768px) {
	.poem > .stanza {
		--wght: 600;
		font-size: -webkit-calc(var(--base-font-size) * 0.9);
		font-size: -moz-calc(var(--base-font-size) * 0.9);
		font-size: calc(var(--base-font-size) * 0.9);
		font-weight: 600;
		padding: 0;
		margin-left: -webkit-calc(var(--body-width-on-desktop) * -0.01);
		margin-left: -moz-calc(var(--body-width-on-desktop) * -0.01);
		margin-left: calc(var(--body-width-on-desktop) * -0.01);
		margin-right: -webkit-calc(var(--body-width-on-desktop) * -0.01);
		margin-right: -moz-calc(var(--body-width-on-desktop) * -0.01);
		margin-right: calc(var(--body-width-on-desktop) * -0.01);
	}

	.stanza.one {
		margin-top: -webkit-calc(var(--body-width-on-desktop) * -0.2325);
		margin-top: -moz-calc(var(--body-width-on-desktop) * -0.2325);
		margin-top: calc(var(--body-width-on-desktop) * -0.2325);
	}

	.stanza.two {
		margin-top: -webkit-calc(var(--body-width-on-desktop) * -0.075);
		margin-top: -moz-calc(var(--body-width-on-desktop) * -0.075);
		margin-top: calc(var(--body-width-on-desktop) * -0.075);
	}

	.stanza.three {
		margin-top: -webkit-calc(var(--body-width-on-desktop) * 0.075);
		margin-top: -moz-calc(var(--body-width-on-desktop) * 0.075);
		margin-top: calc(var(--body-width-on-desktop) * 0.075);
	}

	.stanza.four {
		margin-top: -webkit-calc(var(--body-width-on-desktop) * 0.2325);
		margin-top: -moz-calc(var(--body-width-on-desktop) * 0.2325);
		margin-top: calc(var(--body-width-on-desktop) * 0.2325);
	}
	.glitch {
		font-size: 78%;
	}
}

/* Animations */

@-webkit-keyframes glitch-anim-1 {
	0% {
		-webkit-clip-path: polygon(0 8%, 100% 8%, 100% 67%, 0 67%);
		clip-path: polygon(0 8%, 100% 8%, 100% 67%, 0 67%);
	}

	5% {
		-webkit-clip-path: polygon(0 30%, 100% 30%, 100% 94%, 0 94%);
		clip-path: polygon(0 30%, 100% 30%, 100% 94%, 0 94%);
	}

	10% {
		-webkit-clip-path: polygon(0 33%, 100% 33%, 100% 94%, 0 94%);
